["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 4 do\n            local health = math.random(100, 10000)\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = health,\n                total = 10000,\n                percenthealth = (health / 10000) * 100,\n                shielded = math.random(2) == 1,\n                raidMark = string.format(\"{rt%d}\", i)\n            }\n        end\n    elseif event == \"RAID_TARGET_UPDATE\" then\n        for _, state in pairs(allstates) do\n            local unit = state.unit\n            \n            if UnitExists(unit) then\n                local mark = GetRaidTargetIndex(unit)\n                \n                state.raidMark = mark and string.format(\"{rt%d}\", mark)\n                state.changed = true\n            end\n        end\n        \n        return true\n    elseif event == \"UNIT_HEALTH\" then\n        local unit = ...\n        \n        if unit == \"boss1\" then return end\n        \n        local GUID = UnitGUID(unit)\n        \n        if not GUID then return end\n        \n        local value = UnitHealth(unit)\n        local total = UnitHealthMax(unit)\n        local state = allstates[GUID]\n        \n        if state then\n            state.value = value\n            state.total = total\n            state.percenthealth = (value / total) * 100\n            state.changed = true\n        else\n            local mark = GetRaidTargetIndex(unit)\n            \n            allstates[GUID] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = value,\n                total = total,\n                percenthealth = (value / total) * 100,\n                index = unit,\n                shielded = aura_env.mythic,\n                raidMark = mark and string.format(\"{rt%d}\", mark),\n                unit = unit,\n                autoHide = false\n            }\n        end\n        \n        return true\n    elseif event == \"FRAME_UPDATE\" then\n        if not aura_env.last or aura_env.last < GetTime() - 1 then\n            aura_env.last = GetTime()\n            \n            for GUID, state in pairs(allstates) do\n                local unit = UnitTokenFromGUID(GUID)\n                \n                if UnitExists(unit) then\n                    state.unit = unit\n                    \n                    local mark = GetRaidTargetIndex(unit)\n                    \n                    state.raidMark = mark and string.format(\"{rt%d}\", mark)\n                else\n                    state.show = false\n                end\n                \n                state.changed = true\n            end\n            \n            return true\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if spellID == 1214829 then -- Feedback Nullifier\n            local state = allstates[destGUID]\n            \n            if state then\n                state.shielded = subEvent == \"SPELL_AURA_APPLIED\"\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.mythic = difficultyID == 16\n    end\nend",
