["subeventPrefix"] = "SPELL",
["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            mark = string.format(\"{rt%d}\", math.random(7)),\n            healthPercent = 60 + math.random() * 40,\n        }\n    elseif event == \"UNIT_HEALTH\" then\n        local unit = ...\n        \n        if not unit then return end\n        if unit == \"boss1\" then return end\n        if not aura_env.mark then return end\n        \n        local mark = GetRaidTargetIndex(unit)\n        \n        if mark == aura_env.mark then\n            local healthPercent = (UnitHealth(unit) / UnitHealthMax(unit)) * 100\n            \n            allstates[\"\"] = {\n                show = healthPercent > 60,\n                mark = aura_env.markText,\n                healthPercent = healthPercent,\n                changed = true\n            }\n            \n            return true\n        end\n    elseif event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" or event == \"RAID_TARGET_UPDATE\" then\n        local markFound = false\n        \n        for i = 1, 16 do\n            local unit = \"boss\" .. i\n            \n            if UnitExists(unit) then\n                local mark = GetRaidTargetIndex(unit)\n                \n                if mark == aura_env.mark then\n                    markFound = true\n                    \n                    break\n                end\n            end\n        end\n        \n        if not markFound then\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.mark = nil\n        aura_env.markText = nil\n        \n        for lineCount, line in LiquidWeakAuras:IterateNoteAssignment() do\n            local _, containsPlayerGUID = LiquidWeakAuras:LineToGUIDArray(line)\n            \n            if containsPlayerGUID then\n                aura_env.mark = lineCount\n                aura_env.markText = string.format(\"{rt%d}\", lineCount)\n            end\n        end\n    end\nend",
