{
["trigger"] = {
["use_absorbMode"] = true,
["names"] = {
},
["debuffType"] = "HELPFUL",
["type"] = "custom",
["use_absorbHealMode"] = true,
["subeventSuffix"] = "_CAST_START",
["custom_hide"] = "timed",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local value = 100000 + math.random(900000)\n        local total = 1000000\n        \n        allstates[\"HP\"] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            value = value,\n            total = total,\n            healthpercent = string.format(\"%.1f%%\", (value / total) * 100),\n            hp = true,\n            index = 1,\n            dead = false,\n            mark = string.format(\"{rt%d}\", math.random(8)),\n            autoHide = false\n        }\n        \n        for i = 1, 2 do\n            local affected = math.random(2) == 1\n            local dead = math.random(4) == 1\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 45,\n                expirationTime = GetTime() + (affected and math.random(45) or 0.01),\n                stacks = affected and math.random(20) or 0,\n                hp = false,\n                index = i + 1,\n                unit = \"player\",\n                dead = dead,\n                autoHide = false\n            }\n        end\n    elseif event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" or event == \"RAID_TARGET_UPDATE\" then\n        local markFound = false\n        \n        for i = 1, 16 do\n            local unit = \"boss\" .. i\n            \n            if UnitExists(unit) then\n                local mark = GetRaidTargetIndex(unit)\n                \n                if mark == aura_env.mark then\n                    markFound = true\n                    \n                    break\n                end\n            end\n        end\n        \n        if not markFound then\n            local state = allstates[\"HP\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"UNIT_HEALTH\" then\n        local unit = ...\n        \n        if unit == \"boss1\" then return end\n        if not aura_env.mark then return end\n        \n        local mark = GetRaidTargetIndex(unit)\n        \n        if not mark or mark ~= aura_env.mark then return end\n        \n        local value = UnitHealth(unit)\n        local total = UnitHealthMax(unit)\n        \n        allstates[\"HP\"] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            value = value,\n            total = total,\n            healthpercent = string.format(\"%.1f%%\", (value / total) * 100),\n            hp = true,\n            index = 1,\n            dead = false,\n            mark = string.format(\"{rt%d}\", mark),\n            autoHide = false\n        }\n        \n        return true\n    elseif event == \"TRIGGER\" then\n        local _, triggerStates = ...\n        \n        for id, state in pairs(allstates) do\n            if id ~= \"HP\" then\n                local unit = state.unit\n                \n                state.dead = UnitIsDeadOrGhost(unit)\n                state.changed = true\n            end\n        end\n        \n        for _, triggerState in pairs(triggerStates) do\n            local GUID = triggerState.GUID\n            local state = allstates[GUID]\n            \n            if state then\n                state.dead = false\n                state.stacks = triggerState.stacks or 0\n                state.expirationTime = triggerState.stacks and triggerState.stacks > 0 and triggerState.expirationTime or 0\n                state.changed = true\n            end\n        end\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.mark = nil\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n        \n        for lineCount, line in LiquidWeakAuras:IterateNoteAssignment() do\n            local GUIDs, containsPlayerGUID = LiquidWeakAuras:LineToGUIDArray(line)\n            \n            if containsPlayerGUID then\n                aura_env.mark = lineCount\n                \n                for index, GUID in ipairs(GUIDs) do\n                    allstates[GUID] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = 45,\n                        expirationTime = 0,\n                        stacks = 0,\n                        hp = false,\n                        dead = false,\n                        index = 10 + index,\n                        unit = aura_env.GUIDToUnit[GUID],\n                        autoHide = false\n                    }\n                end\n                \n                return true\n            end\n        end\n    end\nend",
