["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 2 do\n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 22,\n                expirationTime = GetTime() + 22,\n                autoHide = true\n            }\n        end\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local count = WA_GetUnitBuff(\"boss1\", 473227) and 2 or 1 -- Maximum Output\n        \n        for i = 1, count do\n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = aura_env.duration,\n                expirationTime = GetTime() + aura_env.duration,\n                autoHide = true\n            }\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 464399 then -- Electromagnetic Sorting\n            local id = aura_env.id\n            \n            aura_env.muffledDoomsplosionCounts = {}\n            \n            C_Timer.After(4, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id) end)\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_DAMAGE\" and spellID == 465747 then -- Muffled Doomsplosion\n            aura_env.muffledDoomsplosionCounts[destGUID] = (aura_env.muffledDoomsplosionCounts[destGUID] or 0) + 1\n            \n            local state = allstates[aura_env.muffledDoomsplosionCounts[destGUID]]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.duration = difficultyID == 16 and 20 or 24\n        aura_env.muffledDoomsplosionCounts = {} -- [GUID] = count\n    end\nend",
