["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["custom_hide"] = "timed",
["customVariables"] = "{\n    cast = {\n        display = \"Cast\",\n        type = \"bool\"\n    }\n}",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["events"] = "CLEU:SWING_DAMAGE:SPELL_DAMAGE:SPELL_PERIODIC_DAMAGE:SPELL_AURA_APPLIED:SPELL_AURA_REMOVED, NAME_PLATE_UNIT_ADDED",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 4 do\n            local maxHealth = 115530940\n            local health = math.random(maxHealth)\n            \n            allstates[\"cast\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 30,\n                expirationTime = GetTime() + 30,\n                cast = true,\n                index = 1,\n                autoHide = true\n            }\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                total = maxHealth,\n                value = health,\n                index = 10,\n                healthPercent = (health / maxHealth) * 100\n            }\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID, _, _, amount = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            if spellID == 473115 then -- Short Fuse\n                allstates[destGUID] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"static\",\n                    total = aura_env.saved.maxHealth or 115530940,\n                    value = aura_env.saved.maxHealth or 115530940,\n                    index = 10,\n                    healthPercent = 100\n                }\n                \n                if not allstates[\"cast\"] then\n                    allstates[\"cast\"] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = 30,\n                        expirationTime = GetTime() + 30,\n                        cast = true,\n                        index = 1,\n                        autoHide = true\n                    }\n                end\n                \n                return true\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" then\n            if spellID == 473115 then -- Short Fuse\n                -- Hide HP bar for this Bombshell\n                local state = allstates[destGUID]\n                \n                if state then\n                    state.show = false\n                    state.changed = true\n                end\n                \n                -- If no Bombshells remain, hide the cast\n                local bombshellsRemain = false\n                \n                for _, s in pairs(allstates) do\n                    if s.show and not s.cast then\n                        bombshellsRemain = true\n                        \n                        break\n                    end\n                end\n                \n                if not bombshellsRemain and allstates[\"cast\"] then\n                    allstates[\"cast\"].show = false\n                    allstates[\"cast\"].changed = true\n                end\n                \n                return true\n            end\n        else -- SWING_DAMAGE, SPELL_DAMAGE, SPELL_PERIODIC_DAMAGE, \n            local state = allstates[destGUID]\n            \n            if state then\n                amount = subEvent == \"SWING_DAMAGE\" and spellID or amount\n                \n                state.changed = true\n                state.value = state.value - (amount or 0)\n                state.healthPercent = (state.value / state.total) * 100\n                \n                return true\n            end\n        end\n    elseif event == \"NAME_PLATE_UNIT_ADDED\" then\n        local unit = ...\n        local GUID = UnitGUID(unit)\n        local npcID = select(6, strsplit(\"-\", GUID))\n        \n        if npcID == \"231531\" then -- Territorial Bombshell\n            local maxHealth = UnitHealthMax(unit)\n            \n            -- Update max HP for all states\n            if maxHealth ~= aura_env.saved.maxHealth then\n                aura_env.saved.maxHealth = maxHealth\n                \n                for _, state in pairs(allstates) do\n                    if not state.cast then\n                        state.changed = true\n                        state.total = maxHealth\n                        state.healthPercent = (state.value / state.total) * 100\n                    end\n                end\n            end\n            \n            -- Update current HP for this unit\n            local state = allstates[GUID]\n            \n            if state then\n                state.value = UnitHealth(unit)\n                state.healthPercent = (state.value / state.total) * 100\n                state.changed = true\n            end\n            \n            return true\n        end\n    end\nend",
