["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 4 do\n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 24,\n                expirationTime = GetTime() + 24,\n                unit = \"player\",\n                index = i,\n                autoHide = true\n            }\n        end\n    elseif event == \"TRIGGER\" then\n        local _, triggerStates = ...\n        \n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        \n        if not triggerStates then return true end\n        \n        local affected = {}\n        \n        for _, triggerState in pairs(triggerStates) do\n            table.insert(affected, triggerState.GUID)\n        end\n        \n        LiquidWeakAuras:SortTable(affected, false, true) -- Melee > ranged > healers > tanks\n        \n        for _, triggerState in pairs(triggerStates) do\n            local GUID = triggerState.GUID\n            local index = tIndexOf(affected, GUID)\n            \n            allstates[GUID] = {\n                show = triggerState.show,\n                changed = true,\n                progressType = triggerState.progressType,\n                duration = triggerState.duration,\n                expirationTime = triggerState.expirationTime,\n                unit = triggerState.unit,\n                roleIcon = triggerState.roleIcon,\n                index = index,\n                autoHide = true\n            }\n        end\n        \n        return true\n    end\nend",
