["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 24,\n            expirationTime = GetTime() + 24,\n            count = 2,\n            bombsUp = false,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ROLLING_RUBBISH\" then\n        local assignment = ...\n        \n        if assignment == 2 or assignment == 3 then\n            local bombCount = WA_GetUnitBuff(\"boss1\", 473227) and 2 or 1 -- Maximum Output\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 24,\n                expirationTime = GetTime() + 24,\n                bombCount = bombCount,\n                bombsUp = true,\n                autoHide = true\n            }\n            \n            return true\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 464399 then -- Electromagnetic Sorting\n            aura_env.muffledDoomsplosionCounts = {}\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_DAMAGE\" and spellID == 465747 then -- Muffled Doomsplosion\n            aura_env.muffledDoomsplosionCounts[destGUID] = (aura_env.muffledDoomsplosionCounts[destGUID] or 0) + 1\n            \n            local state = allstates[\"\"]\n            \n            if state and state.bombCount == aura_env.muffledDoomsplosionCounts[destGUID] then\n                state.bombsUp = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \