["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local assignment = math.random(4)\n        local set = math.random(2, 5)\n        \n        for i = 1, 4 do\n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = aura_env.config.duration,\n                expirationTime = GetTime() + aura_env.config.duration,\n                color = aura_env.colors[set][i], -- This field is only used if assigned is true\n                assigned = i == assignment,\n                direction = i,\n                flip = aura_env.config.flip,\n                autoHide = true\n            }\n        end\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env.set = aura_env.set + 1\n        \n        LiquidWeakAuras:SortTable(aura_env.affected, false, true) -- Melee > ranged > healers > tanks\n        \n        local assignment = tIndexOf(aura_env.affected, WeakAuras.myGUID)\n        \n        if assignment then\n            for i = 1, 4 do -- On normal/heroic, the tank doesn't get assigned\n                local color = aura_env.mythic and aura_env.colors[aura_env.set] and aura_env.colors[aura_env.set][i] or \"GREEN\"\n                local mark = aura_env.colorToMark[color]\n                local assigned = i == assignment\n                \n                allstates[i] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = aura_env.config.duration,\n                    expirationTime = GetTime() + aura_env.config.duration,\n                    assigned = assigned,\n                    color = color, -- This field is only used if assigned is true\n                    direction = i,\n                    mark = mark,\n                    flip = aura_env.config.flip,\n                    autoHide = true\n                }\n                \n                if aura_env.config.tts and assigned then\n                    LiquidWeakAuras:PlaySound(mark)\n                end\n                \n                WeakAuras.ScanEvents(\"LIQUID_ROLLING_RUBBISH\", assignment, mark, aura_env.config.duration, assigned)\n            end\n        end\n        \n        aura_env.affected = {}\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 465346 then -- Sorted\n            table.insert(aura_env.affected, destGUID)\n            \n            if #aura_env.affected == 1 then\n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.affected = {}\n        aura_env.mythic = difficultyID == 16\n        aura_env.set = 0\n    end\nend",
