{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["custom_hide"] = "timed",
["customVariables"] = "{\n    color = {\n        display = \"Color\",\n        type = \"select\",\n        values = {\n            BLUE = \"|cff0000ffBlue|r\",\n            RED = \"|cffff0000Red|r\"\n        }\n    },\n    spacer = {\n        display = \"Spacer\",\n        type = \"bool\"\n    }\n}",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["events"] = "ENCOUNTER_START, UNIT_SPELLCAST_SUCCEEDED:boss, CLEU:SPELL_AURA_APPLIED:SPELL_AURA_APPLIED_DOSE:SPELL_AURA_REMOVED, LIQUID_CUSTOM_EVENT",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        -- Blue\n        for i = 1, 2 do\n            table.insert(\n                allstates,\n                {\n                    show = true,\n                    changed = true,\n                    color = \"BLUE\",\n                    index = i\n                }\n            )\n        end\n        \n        -- Red\n        for i = 1, 2 do\n            table.insert(\n                allstates,\n                {\n                    show = true,\n                    changed = true,\n                    color = \"RED\",\n                    index = 400 + i\n                }\n            )\n        end\n        \n        -- Spacer\n        allstates[\"SPACER\"] = {\n            show = true,\n            changed = true,\n            spacer = true,\n            index = 300\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        for _, state in pairs(allstates) do\n            if not state.spacer then\n                state.show = false\n                state.changed = true\n            end\n        end\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if (subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\") and spellID == 1218342 then -- Unstable Shrapnel\n            local unit = aura_env.GUIDToUnit[destGUID]\n            local color = WA_GetUnitDebuff(unit, 1216911) and \"BLUE\" or WA_GetUnitDebuff(unit, 1216934) and \"RED\" or \"GRAY\"\n            \n            for _, state in pairs(allstates) do\n                if state.color == color then\n                    state.show = false\n                    state.changed = true\n                    \n                    return true\n                end\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellID == 1218318 then -- Bleeding Edge\n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1217231 then -- Foot-Blasters\n            if aura_env.difficultyID == 16 then -- Mythic\n                -- Blue\n                for i = 1, 2 do\n                    table.insert(\n                        allstates,\n                        {\n                            show = true,\n                            changed = true,\n                            color = \"BLUE\",\n                            index = i,\n                        }\n                    )\n                end\n                \n                -- Red\n                for i = 1, 2 do\n                    table.insert(\n                        allstates,\n                        {\n                            show = true,\n                            changed = true,\n                            color = \"RED\",\n                            index = 400 + i,\n                        }\n                    )\n                end\n            else\n                for i = 1, 4 do\n                    table.insert(\n                        allstates,\n                        {\n                            show = true,\n                            changed = true,\n                            color = \"GRAY\",\n                            index = i\n                        }\n                    )\n                end\n            end\n            \n            return true\n        elseif spellID == 1218418 then -- Wire Transfer\n            C_Timer.After(3, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id) end)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.difficultyID = select(3, ...)\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n        \n        -- Create a spacer for mythic (to visually split the red/blue list)\n        if aura_env.difficultyID == 16 then\n            allstates[\"SPACER\"] = {\n                show = true,\n                changed = true,\n                spacer = true,\n                index = 300\n            }\n            \n            return true\n        end\n    end\nend",
