["init"] = {
["custom"] = "aura_env.roleIcons = {\n    MELEE = \"|A:UI-LFG-RoleIcon-DPS:16:16|a\",\n    RANGED = \"|A:UI-LFG-RoleIcon-RangedDPS:16:16|a\",\n    HEALER = \"|A:UI-LFG-RoleIcon-Healer:16:16|a\",\n    TANK = \"|A:UI-LFG-RoleIcon-Tank:16:16|a\"\n}\n\nfunction aura_env.SortFunction(guidA, guidB)\n    local unitA = aura_env.GUIDToUnit[guidA]\n    local unitB = aura_env.GUIDToUnit[guidB]\n    \n    local roleA = UnitGroupRolesAssigned(unitA)\n    local roleB = UnitGroupRolesAssigned(unitB)\n    \n    local positionA = LiquidWeakAuras:Spec(unitA)\n    local positionB = LiquidWeakAuras:Spec(unitB)\n    \n    if positionA ~= positionB then\n        return positionA == \"m\"\n    elseif roleA ~= roleB then\n        if positionA == \"m\" then -- Melee are sorted damager < tank < healer\n            if roleA == \"DAMAGER\" or roleB == \"DAMAGER\" then\n                return roleA == \"DAMAGER\"\n            else\n                return roleA == \"TANK\"\n            end\n        else -- Ranged are sorted healer < damager\n            return roleA == \"HEALER\"\n        end\n    else\n        return guidA < guidB\n    end\nend",
