["custom_hide"] = "timed",
["customVariables"] = "{\n    color = {\n        display = \"Color\",\n        type = \"select\",\n        values = {\n            BLUE = \"|cff0000ffBlue|r\",\n            RED = \"|cffff0000Red|r\"\n        }\n    }\n}",
