["events"] = "TRIGGER:2, ENCOUNTER_START, LIQUID_CUSTOM_EVENT, CLEU:SPELL_AURA_APPLIED:SPELL_AURA_REMOVED",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        -- Blue\n        for i = 1, aura_env.config.playerCount do\n            allstates[i] = {\n                show = true,\n                changed = true,\n                unit = \"player\",\n                color = \"BLUE\",\n                roleIcon = i <= aura_env.config.playerCount / 2 and aura_env.roleIcons.MELEE or aura_env.roleIcons.RANGED,\n                index = 100 + i\n            }\n        end\n        \n        -- Red\n        for i = 1, aura_env.config.playerCount do\n            allstates[aura_env.config.playerCount + i] = {\n                show = true,\n                changed = true,\n                unit = \"player\",\n                color = \"RED\",\n                roleIcon = i <= aura_env.config.playerCount / 2 and aura_env.roleIcons.MELEE or aura_env.roleIcons.RANGED,\n                index = 500 + i\n            }\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and (spellID == 1216911 or spellID == 1216934) then\n            if not aura_env.updateQueued then\n                aura_env.updateQueued = true\n                \n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id) end)\n            end\n            \n        elseif subEvent == \"SPELL_AURA_REMOVED\" and (spellID == 1216911 or spellID == 1216934) then\n            local state = allstates[destGUID]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env.updateQueued = false\n        \n        local triggerStates = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 2)\n        \n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        \n        if not triggerStates then return true end\n        \n        local availableGUIDs = {\n            GRAY = {},\n            BLUE = {},\n            RED = {}\n        }\n        \n        for _, state in pairs(triggerStates) do\n            local unit = state.unit\n            \n            if unit then\n                local color = WA_GetUnitDebuff(unit, 1216911) and \"BLUE\" or WA_GetUnitDebuff(unit, 1216934) and \"RED\" or \"GRAY\"\n                local GUID = UnitGUID(unit)\n                \n                table.insert(availableGUIDs[color], GUID)\n            end\n        end\n        \n        -- Sort available GUIDs melee > tank > melee healers > ranged healers > ranged\n        -- We want to display players from both ends of the priority\n        table.sort(availableGUIDs.GRAY, aura_env.SortFunction)\n        table.sort(availableGUIDs.BLUE, aura_env.SortFunction)\n        table.sort(availableGUIDs.RED, aura_env.SortFunction)\n        \n        local grayGUIDs = {}\n        local blueGUIDs = {}\n        local redGUIDs = {}\n        \n        -- Melee players\n        for i = 1, math.floor(aura_env.config.playerCount / 2) do\n            tInsertUnique(grayGUIDs, availableGUIDs.GRAY[i])\n            tInsertUnique(blueGUIDs, availableGUIDs.BLUE[i])\n            tInsertUnique(redGUIDs, availableGUIDs.RED[i])\n        end\n        \n        -- Ranged players\n        for i = math.ceil(aura_env.config.playerCount / 2) - 1, 0, -1 do\n            tInsertUnique(grayGUIDs, availableGUIDs.GRAY[#availableGUIDs.GRAY - i])\n            tInsertUnique(blueGUIDs, availableGUIDs.BLUE[#availableGUIDs.BLUE - i])\n            tInsertUnique(redGUIDs, availableGUIDs.RED[#availableGUIDs.RED - i])\n        end\n        \n        if aura_env.difficultyID == 16 then -- Mythic\n            -- Blue\n            for i, GUID in ipairs(blueGUIDs) do\n                local unit = aura_env.GUIDToUnit[GUID]\n                local roleIcon = aura_env.guidToRoleIcon[GUID] or \"\"\n                \n                allstates[GUID] = {\n                    show = true,\n                    changed = true,\n                    unit = unit,\n                    roleIcon = roleIcon,\n                    color = \"BLUE\",\n                    index = 100 + i\n                }\n            end\n            \n            -- Red\n            for i, GUID in ipairs(redGUIDs) do\n                local unit = aura_env.GUIDToUnit[GUID]\n                local roleIcon = aura_env.guidToRoleIcon[GUID] or \"\"\n                \n                allstates[GUID] = {\n                    show = true,\n                    changed = true,\n                    unit = unit,\n                    roleIcon = roleIcon,\n                    color = \"RED\",\n                    index = 500 + i\n                }\n            end\n        else\n            for i, GUID in ipairs(grayGUIDs) do\n                local unit = aura_env.GUIDToUnit[GUID]\n                local roleIcon = aura_env.guidToRoleIcon[GUID] or \"\"\n                \n                allstates[GUID] = {\n                    show = true,\n                    changed = true,\n                    unit = unit,\n                    roleIcon = roleIcon,\n                    color = \"GRAY\",\n                    index = 100 + i\n                }\n            end\n        end\n        \n        return true\n    elseif event == \"TRIGGER\" then\n        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.difficultyID = select(3, ...)\n        aura_env.GUIDToUnit = {}\n        aura_env.updateQueued = false\n        aura_env.guidToRoleIcon = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            local role = UnitGroupRolesAssigned(unit)\n            local position = LiquidWeakAuras:Spec(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n            \n            if role == \"DAMAGER\" and position == \"m\" then\n                aura_env.guidToRoleIcon[GUID] = aura_env.roleIcons.MELEE\n            elseif role == \"DAMAGER\" and position == \"r\" then\n                aura_env.guidToRoleIcon[GUID] = aura_env.roleIcons.RANGED\n            elseif role == \"HEALER\" then\n                aura_env.guidToRoleIcon[GUID] = aura_env.roleIcons.HEALER\n            elseif role == \"TANK\" then\n                aura_env.guidToRoleIcon[GUID] = aura_env.roleIcons.TANK\n            end\n        end\n    end\nend",
