["subeventPrefix"] = "SPELL",
["events"] = "LIQUID_FOOT_BLASTER_ASSIGNMENT, ENCOUNTER_START",
["spellIds"] = {
},
["subeventSuffix"] = "_CAST_START",
["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 4 do\n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                total = 1,\n                value = 1,\n                color = i % 2 == 0 and \"RED\" or \"BLUE\",\n                mark = string.format(\"{rt%d}\", i),\n                exploded = false,\n                unit = \"player\",\n                index = i\n            }\n        end\n    elseif event == \"LIQUID_FOOT_BLASTER_ASSIGNMENT\" then\n        local _, assignments = ...\n        local remaining = #assignments\n        \n        -- If a mine was popped, show the Unstable Explosion duration\n        if remaining < aura_env.previousRemaining then\n            for i = 1, 4 do\n                local state = allstates[i]\n                \n                if state then\n                    state.changed = true\n                    state.progressType = \"timed\"\n                    state.duration = 2\n                    state.expirationTime = GetTime() + 2\n                    state.exploded = true\n                    state.autoHide = true\n                    \n                    break\n                end\n            end\n        end\n        \n        for _, entry in ipairs(assignments) do\n            local index = entry.order\n            \n            allstates[index] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                total = 1,\n                value = 1,\n                color = entry.color,\n                mark = string.format(\"{rt%d}\", entry.marker),\n                exploded = false,\n                unit = aura_env.GUIDToUnit[entry.GUID],\n                index = index\n            }\n        end\n        \n        aura_env.previousRemaining = remaining\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.previousRemaining = 0\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n    end\nend",
["names"] = {
},
