["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6,\n            expirationTime = GetTime() + 6,\n            eligible = math.random(2) == 1,\n            autoHide = true\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1216509 then -- Screw Up\n            aura_env.nextEligibleTimes[destGUID] = GetTime() + 90\n        elseif subEvent == \"UNIT_DIED\" and aura_env.GUIDToUnit[destGUID] then\n            aura_env.nextEligibleTimes[destGUID] = 0\n        end\n    elseif event == \"TRIGGER\" then\n        local _, triggerStates = ...\n        local state = triggerStates and triggerStates[\"\"]\n        \n        if not state then return end\n        if not state.show then return end\n        if not state.expirationTime then return end\n        \n        local availableGUIDs = {} -- Available GUIDs, accounting for dead players\n        \n        for GUID, t in pairs(aura_env.nextEligibleTimes) do\n            local unit = aura_env.GUIDToUnit[GUID]\n            local alive = not UnitIsDeadOrGhost(unit)\n            local visible = UnitIsVisible(unit)\n            local position = LiquidWeakAuras:Spec(unit)\n            local isRanged = position == \"r\"\n            local eligible = t < state.expirationTime\n            \n            if alive and visible and eligible and isRanged then\n                table.insert(availableGUIDs, GUID)\n            end\n        end\n        \n        local eligible = tContains(availableGUIDs, WeakAuras.myGUID) or #availableGUIDs < 3\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = state.duration,\n            expirationTime = state.expirationTime,\n            eligible = eligible,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.nextEligibleTimes = {} -- GUID and when they are available for getting Screw Up\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n            aura_env.nextEligibleTimes[GUID] = 0\n        end\n    end\nend",
