["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local textColor = math.random(2) == 1 and \"ff36a5f5\" or \"fff0264b\"\n        local text = math.random(2) == 1 and \"Same\" or \"Switch\"\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            text = string.format(\"|c%s%s|r\", textColor, text),\n            autoHide = true\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and destGUID == WeakAuras.myGUID then\n            if spellID == 1217358 or spellID == 1217357 then -- Polarization Generator\n                if aura_env.previousPolarity then\n                    local newPolarity = spellID == 1217357 and \"BLUE\" or \"RED\"\n                    local samePolarity = newPolarity == aura_env.previousPolarity\n                    \n                    if samePolarity and aura_env.config.switchOnly then return end\n                    \n                    local textColor = spellID == 1217357 and \"ff36a5f5\" or \"fff0264b\"\n                    local text = samePolarity and \"Same\" or \"Switch\"\n                    \n                    allstates[\"\"] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = 4,\n                        expirationTime = GetTime() + 4,\n                        text = string.format(\"|c%s%s|r\", textColor, text),\n                        autoHide = true\n                    }\n                    \n                    if aura_env.config.tts then\n                        if samePolarity then\n                            LiquidWeakAuras:PlaySound(\"same\")\n                        else\n                            LiquidWeakAuras:PlaySound(\"switch\")\n                        end\n                    end\n                else -- First polarity of the fight\n                    local textColor = spellID == 1217357 and \"ff36a5f5\" or \"fff0264b\"\n                    local colorName = spellID == 1217357 and \"Blue\" or \"Red\"\n                    \n                    allstates[\"\"] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = 4,\n                        expirationTime = GetTime() + 4,\n                        text = string.format(\"|c%s%s|r\", textColor, colorName),\n                        autoHide = true\n                    }\n                end\n                \n                return true\n            elseif spellID == 1216911 or spellID == 1216934 then -- Posi-Polarization/Nega-Polarization\n                aura_env.previousPolarity = spellID == 1216911 and \"BLUE\" or \"RED\"\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.previousPolarity = nil\n    end\nend",
