["custom"] = "local tts = aura_env.state and aura_env.state.tts\n\nif tts and aura_env.config.tts then\n    C_Timer.After(2, function() LiquidWeakAuras:PlaySound(tts) end) -- To not overlap with the Polarity switch TTS\nend",
