["init"] = {
["custom"] = "local debuffDuration = 2\nlocal gracePeriod = 0.2\n\nlocal tts = {\n    [1] = \"soak star\",\n    [2] = \"soak circle\",\n    [3] = \"soak diamond\",\n    [4] = \"soak triangle\",\n    [5] = \"soak moon\",\n    [6] = \"soak square\",\n    [7] = \"soak cross\",\n    [8] = \"soak skull\"\n}\n\n-- Time from phase start (either pull or Bleeding Edge falling off) until first pop (per set)\nlocal heroicPopOffsets = {\n    15, 77\n}\n\nlocal mythicPopOffsets = {\n    15, 48, 78\n}\n\nlocal heroicPopOrder = {\n    -- First P1\n    [1] = {\n        {distance = \"CLOSE\", marker = 3},\n        {distance = \"CLOSE\", marker = 4},\n        {distance = \"FAR\", marker = 7},\n        {distance = \"FAR\", marker = 8},\n    },\n    [2] = {\n        {distance = \"CLOSE\", marker = 1},\n        {distance = \"CLOSE\", marker = 2},\n        {distance = \"FAR\", marker = 5},\n        {distance = \"FAR\", marker = 6},\n    },\n    \n    -- Second P1\n    [3] = {\n        {distance = \"CLOSE\", marker = 8},\n        {distance = \"CLOSE\", marker = 7},\n        {distance = \"FAR\", marker = 6},\n        {distance = \"FAR\", marker = 5},\n    },\n    [4] = {\n        {distance = \"CLOSE\", marker = 4},\n        {distance = \"CLOSE\", marker = 3},\n        {distance = \"FAR\", marker = 2},\n        {distance = \"FAR\", marker = 1},\n    },\n    \n    -- Third P1\n    [5] = {\n        {distance = \"CLOSE\", marker = 7},\n        {distance = \"CLOSE\", marker = 8},\n        {distance = \"FAR\", marker = 3},\n        {distance = \"FAR\", marker = 4},\n    },\n    [6] = {\n        {distance = \"CLOSE\", marker = 5},\n        {distance = \"CLOSE\", marker = 6},\n        {distance = \"FAR\", marker = 3},\n        {distance = \"FAR\", marker = 4},\n    },\n}\n\nlocal mythicPopOrder = {\n    -- First P1\n    [1] = {\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 3},\n        {distance = \"CLOSE\", color = \"RED\", marker = 4},\n        {distance = \"FAR\", color = \"BLUE\", marker = 7},\n        {distance = \"FAR\", color = \"RED\", marker = 8},\n    },\n    [2] = {\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 2},\n        {distance = \"CLOSE\", color = \"RED\", marker = 1},\n        {distance = \"FAR\", color = \"BLUE\", marker = 6},\n        {distance = \"FAR\", color = \"RED\", marker = 5},\n    },\n    [3] = {\n        {distance = \"FAR\", color = \"BLUE\", marker = 7},\n        {distance = \"FAR\", color = \"RED\", marker = 8},\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 2},\n        {distance = \"CLOSE\", color = \"RED\", marker = 1},\n    },\n    \n    -- Second P1\n    [4] = {\n        {distance = \"FAR\", color = \"BLUE\", marker = 6},\n        {distance = \"FAR\", color = \"RED\", marker = 5},\n        {distance = \"CLOSE\", color = \"RED\", marker = 8},\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 7},\n    },\n    [5] = {\n        {distance = \"FAR\", color = \"RED\", marker = 4},\n        {distance = \"FAR\", color = \"BLUE\", marker = 3},\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 6},\n        {distance = \"CLOSE\", color = \"RED\", marker = 5},\n    },\n    [6] = {\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 2},\n        {distance = \"CLOSE\", color = \"RED\", marker = 1},\n        {distance = \"FAR\", color = \"BLUE\", marker = 6},\n        {distance = \"FAR\", color = \"RED\", marker = 5},\n    },\n    \n    -- Third P1\n    [7] = {\n        {distance = \"FAR\", color = \"BLUE\", marker = 6},\n        {distance = \"FAR\", color = \"RED\", marker = 5},\n        {distance = \"CLOSE\", color = \"RED\", marker = 4},\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 3},\n    },\n    [8] = {\n        {distance = \"FAR\", color = \"BLUE\", marker = 6},\n        {distance = \"FAR\", color = \"RED\", marker = 5},\n        {distance = \"CLOSE\", color = \"RED\", marker = 8},\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 7},\n    },\n    [9] = {\n        {distance = \"FAR\", color = \"BLUE\", marker = 2},\n        {distance = \"FAR\", color = \"RED\", marker = 1},\n        {distance = \"CLOSE\", color = \"BLUE\", marker = 7},\n        {distance = \"CLOSE\", color = \"RED\", marker = 8},\n    },\n}\n\nlocal function GetUnitColor(unit)\n    if not aura_env.mythic then return end\n    \n    if WA_GetUnitDebuff(unit, 1217357) or WA_GetUnitDebuff(unit, 1216911) then\n        return \"BLUE\"\n    end\n    \n    if WA_GetUnitDebuff(unit, 1217358) or WA_GetUnitDebuff(unit, 1216934) then\n        return \"RED\"\n    end\n    \n    local GUID = UnitGUID(unit)\n    local currentTime = GetTime()\n    \n    if not GUID then return end\n    \n    -- Handle the edge case where the color is queried during the second between Polarization Generator and Posi/Nega-Polarization\n    local blueApplicationTime = aura_env.polarizationGeneratorApplications.BLUE[GUID]\n    \n    if blueApplicationTime and (blueApplicationTime + 4.5 > currentTime) then\n        return \"BLUE\"\n    end\n    \n    local redApplicationTime = aura_env.polarizationGeneratorApplications.RED[GUID]\n    \n    if redApplicationTime and (redApplicationTime + 4.5 > currentTime) then\n        return \"RED\"\n    end\nend\n\n-- Returns whether a given GUID is eligible to soak a trap\nlocal function IsEligibleToSoak(GUID, entry)\n    local unit = aura_env.GUIDToUnit[GUID]\n    local alive = not UnitIsDeadOrGhost(unit)\n    local connected = UnitIsConnected(unit)\n    local visible = UnitIsVisible(unit)\n    local unitColor = GetUnitColor(unit)\n    local _, _, _, _, _, shrapnelExpirationTime = WA_GetUnitDebuff(unit, 1218342)\n    local assigned = false\n    \n    for _, e in ipairs(aura_env.assignments) do\n        if e.GUID and e.GUID == GUID and e.order ~= entry.order then\n            assigned = true\n            \n            break\n        end\n    end\n    \n    return alive and connected and visible and unitColor == entry.color and (shrapnelExpirationTime or 0) < entry.time and not assigned\nend\n\n-- Returns the GUID of the best player (based on priority/debuffs/etc.) to soak the given trap\nlocal function AssignGUID(entry)\n    -- If there is a priority override for this entry, try to assign from that first\n    local priorityOverride = aura_env.priorityOverrides[entry.set] and aura_env.priorityOverrides[entry.set][entry.distance]\n    \n    if priorityOverride then\n        for _, GUID in ipairs(priorityOverride) do\n            if IsEligibleToSoak(GUID, entry) then\n                entry.GUID = GUID\n                \n                return\n            end\n        end\n    end\n    \n    -- Assign using the default priority\n    local priority = aura_env.priority[entry.distance]\n    \n    for _, GUID in ipairs(priority) do\n        if IsEligibleToSoak(GUID, entry) then\n            entry.GUID = GUID\n            \n            return\n        end\n    end\n    \n    -- Assign using fallback priority\n    local fallbackPriority = aura_env.fallbackPriority\n    \n    for _, GUID in ipairs(fallbackPriority) do\n        if IsEligibleToSoak(GUID, entry) then\n            entry.GUID = GUID\n            \n            return\n        end\n    end\nend\n\n-- Adds the next set of bombs to the assignment table\nfunction aura_env.AddNewSet(allstates, encounterStart)\n    if not aura_env.encounterStart then return end\n    \n    if encounterStart ~= aura_env.encounterStart then return end\n    \n    aura_env.set = aura_env.set + 1\n    \n    local newSet = aura_env.mythic and mythicPopOrder[aura_env.set] or heroicPopOrder[aura_env.set]\n    \n    if not newSet then return end\n    \n    local popOffsets = aura_env.mythic and mythicPopOffsets or heroicPopOffsets\n    local setsPerPhase = #popOffsets\n    local popOffset = popOffsets[((aura_env.set - 1) % setsPerPhase) + 1]\n    \n    if newSet then\n        for i, entry in ipairs(newSet) do\n            table.insert(\n                aura_env.assignments,\n                {\n                    distance = entry.distance,\n                    color = entry.color,\n                    marker = entry.marker,\n                    time = aura_env.phaseTime + popOffset + (i - 1) * (debuffDuration + gracePeriod),\n                    set = aura_env.set,\n                    order = i\n                }\n            )\n        end\n    end\n    \n    aura_env.UpdateAssignments(allstates)\nend\n\nfunction aura_env.UpdateState(allstates)\n    local currentTime = GetTime()\n    \n    for i, entry in ipairs(aura_env.assignments) do\n        if entry.GUID == WeakAuras.myGUID then\n            local popTime = entry.time\n            \n            if entry.order > 1 then -- If we are not popping the first trap in the set, let pop time depend on last pop\n                local popsRemaining = i - 1 -- Number of pops remaining before ours\n                local nextPopTime = math.max(\n                    aura_env.lastPop + (debuffDuration + gracePeriod), -- Shrapnel is up (or we are in grace period)\n                    currentTime, -- Grace period is over\n                    aura_env.assignments[1].order == 1 and aura_env.assignments[1].time or 0 -- Waiting for initial pop\n                )\n                \n                popTime = nextPopTime + popsRemaining * (debuffDuration + gracePeriod) -- Minimum time to our pop if the person before us popped right now\n            end\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 15,\n                expirationTime = popTime,\n                marker = string.format(\"{rt%d}\", entry.marker),\n                tts = tts[entry.marker],\n                order = entry.order,\n                autoHide = false\n            }\n            \n            return true\n        end\n    end\n    \n    -- Hide state\n    local state = allstates[\"\"]\n    \n    if state then\n        state.show = false\n        state.changed = true\n        \n        return true\n    end\n    \n    return false\nend\n\nfunction aura_env.UpdateAssignments(allstates)\n    -- Check if assigned players are still eligible to soak their assigned trap\n    -- This may not be the case if they died or their polarity changed\n    for _, entry in ipairs(aura_env.assignments) do\n        if entry.GUID then\n            if not IsEligibleToSoak(entry.GUID, entry) then\n                entry.GUID = nil\n            end\n        end\n    end\n    \n    -- Attempt to assign the entries that have priority overrides first\n    for _, entry in ipairs(aura_env.assignments) do\n        local hasOverride = aura_env.priorityOverrides[entry.set] and aura_env.priorityOverrides[entry.set][entry.distance]\n        \n        if hasOverride and not entry.GUID then\n            AssignGUID(entry)\n        end\n    end\n    \n    -- For every trap entry that doesn't have a soaker assigned to it yet, assign someone\n    for _, entry in ipairs(aura_env.assignments) do\n        if not entry.GUID then\n            AssignGUID(entry)\n        end\n    end\n    \n    WeakAuras.ScanEvents(\"LIQUID_FOOT_BLASTER_ASSIGNMENT\", aura_env.set, aura_env.assignments, aura_env.mythic) -- Broadcast assignments\n    \n    return aura_env.UpdateState(allstates)\nend\n\nfunction aura_env.QueueHeroicSets(allstates)\n    C_Timer.After(5, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end)\n    C_Timer.After(67, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end)\nend",
