["unit"] = "player",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 10,\n            expirationTime = GetTime() + 10,\n            marker = string.format(\"{rt%d}\", math.random(8)),\n            order = math.random(4),\n            autoHide = false\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" then\n            if spellID == 1216406 then -- Unstable Explosion\n                local currentTime = GetTime()\n                \n                if currentTime - aura_env.lastPop > 0.5 then\n                    aura_env.lastPop = currentTime\n                    \n                    table.remove(aura_env.assignments, 1)\n                    \n                    return aura_env.UpdateAssignments(allstates)\n                end\n            elseif spellID == 1217357 then -- Polarization Generator (blue)\n                aura_env.polarizationGeneratorApplications.BLUE[destGUID] = GetTime()\n            elseif spellID == 1217358 then -- Polarization Generator (red)\n                aura_env.polarizationGeneratorApplications.RED[destGUID] = GetTime()\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellID == 1218318 then -- Bleeding Edge (P1 start)            \n            aura_env.polarizationGeneratorCount = 0\n            aura_env.phaseTime = GetTime()\n            \n            if not aura_env.mythic then\n                aura_env.QueueHeroicSets(allstates)\n            end\n        elseif subEvent == \"UNIT_DIED\" and aura_env.GUIDToUnit[destGUID] then\n            return aura_env.UpdateAssignments(allstates)\n        elseif subEvent == \"SPELL_CAST_SUCCESS\" and spellID == 1217355 then -- Polarization Generator (mythic only)\n            aura_env.polarizationGeneratorCount = aura_env.polarizationGeneratorCount + 1\n            \n            if aura_env.polarizationGeneratorCount == 1 then\n                if aura_env.set == 3 then -- 5th set should show extra early (queued when we are on set 3)\n                    C_Timer.After(0.2, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- 1st Foot-Blaster set\n                    C_Timer.After(30, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- Queue up 2nd Foot-Blaster set\n                elseif aura_env.set == 6 then -- 8th set should show extra early (queued up when we are on set 6)\n                    C_Timer.After(0.2, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- 1st Foot-Blaster set\n                    C_Timer.After(30, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- Queue up 2nd Foot-Blaster set\n                else\n                    C_Timer.After(0.2, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- 1st Foot-Blaster set\n                    C_Timer.After(38, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- Queue up 2nd Foot-Blaster set\n                end\n            elseif aura_env.polarizationGeneratorCount == 2 then\n                C_Timer.After(0.2, function() aura_env.AddNewSet(allstates, aura_env.encounterStart) end) -- 3rd Foot-Blaster set\n            else\n                C_Timer.After(0.2, function() aura_env.UpdateAssignments(allstates) end) -- Just in case there is still a trap remaining\n            end\n        end\n    elseif event == \"FRAME_UPDATE\" then\n        if not aura_env.last or aura_env.last < GetTime() - 0.2 then\n            aura_env.last = GetTime()\n            \n            return aura_env.UpdateState(allstates)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.encounterStart = GetTime() -- Used to verify that AddNewSet calls come from this pull\n        aura_env.mythic = difficultyID == 16\n        aura_env.lastPop = 0 -- Used to calculate remaining time on Shrapnel debuff\n        aura_env.assignments = {}\n        aura_env.phaseTime = GetTime() -- Start time of the phase (either pull or Bleeding Edge falling off)\n        aura_env.polarizationGeneratorCount = 0 -- Number of Polarization Generator casts (resets every P1 start)\n        aura_env.set = 0 -- The last set of Foot-Blasters that we added assignments for\n        aura_env.priority = {CLOSE = {}, FAR = {}} -- Priority for soaking traps (CLOSE and FAR subtables)\n        aura_env.priorityOverrides = {}\n        aura_env.GUIDToUnit = {}\n        aura_env.fallbackPriority = {}\n        aura_env.polarizationGeneratorApplications = {BLUE = {}, RED = {}}\n        \n        -- GUID to unit\n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n        \n        -- MRT assignments\n        for lineCount, line in LiquidWeakAuras:IterateNoteAssignment() do\n            if lineCount == 1 then\n                aura_env.priority.CLOSE = LiquidWeakAuras:LineToGUIDArray(line)\n            else\n                local set, distance, GUIDs = line:match(\"(%d-)%s(.-)%s(.+)\")\n                \n                set = set and tonumber(set)\n                GUIDs = GUIDs and LiquidWeakAuras:LineToGUIDArray(GUIDs)\n                \n                if set and (distance == \"M\" or distance == \"R\") and GUIDs and next(GUIDs) then\n                    distance = distance == \"M\" and \"CLOSE\" or \"FAR\"\n                    \n                    if not aura_env.priorityOverrides[set] then aura_env.priorityOverrides[set] = {} end\n                    \n                    aura_env.priorityOverrides[set][distance] = GUIDs\n                end\n            end\n        end\n        \n        -- If no note is supplied, just include all group members\n        if not next(aura_env.priority.CLOSE) then\n            for unit in WA_IterateGroupMembers() do\n                local GUID = UnitGUID(unit)\n                \n                table.insert(aura_env.priority.CLOSE, GUID)\n            end\n            \n            -- Simple melee first for close soaks, ranged first for far soaks\n            LiquidWeakAuras:SortTable(aura_env.priority.CLOSE)\n        end\n        \n        -- Reverse order for ranged\n        for _, GUID in ipairs_reverse(aura_env.priority.CLOSE) do\n            table.insert(aura_env.priority.FAR, GUID)\n        end\n        \n        if not aura_env.mythic then\n            aura_env.QueueHeroicSets(allstates)\n        end\n        \n        -- Save a sorted list (melee -> ranged) of GUIDs that are not listed on the general note at all\n        -- We only fall back to these GUIDs if it's impossible to assign all trap with the GUIDs that were listed\n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            if not tContains(aura_env.priority.CLOSE, GUID) and not tContains(aura_env.priority.FAR, GUID) then\n                table.insert(aura_env.fallbackPriority, GUID)\n            end\n        end\n        \n        -- Sort dps > healers > tanks\n        table.sort(\n            aura_env.fallbackPriority,\n            function(guidA, guidB)\n                local unitA = aura_env.GUIDToUnit[guidA]\n                local unitB = aura_env.GUIDToUnit[guidB]\n                \n                local roleA = UnitGroupRolesAssigned(unitA)\n                local roleB = UnitGroupRolesAssigned(unitB)\n                \n                if roleA ~= roleB then\n                    return roleA < roleB\n                else\n                    return guidA < guidB\n                end\n            end\n        )\n    end\nend",
