["customGrow"] = "function(newPositions, activeRegions)\n    local mapWidth, mapHeight\n    \n    -- Get map size\n    for regionCount, regionData in ipairs(activeRegions) do\n        if regionData.id:match(\"map\") then\n            mapWidth = regionData.region.width\n            mapHeight = regionData.region.height\n            \n            newPositions[regionCount] = {0, 0}\n            \n            break\n        end\n    end\n    \n    if mapWidth and mapHeight then\n        for regionCount, regionData in ipairs(activeRegions) do\n            if not regionData.id:match(\"map\") then\n                local position = regionData.region.state.position\n                \n                newPositions[regionCount] = {\n                    mapWidth * position[1] - 0.5 * mapWidth,\n                    mapHeight * position[2] - 0.5 * mapHeight\n                }\n            end\n        end\n    end\nend",
