["custom"] = "local auraWidth = WeakAuras.GetData(aura_env.id).width\nlocal conveyorWidth = 0.12 * auraWidth\n\nlocal backgroundColor = {\n    r = 0.25,\n    g = 0.25,\n    b = 0.25\n}\n\nlocal conveyorColor = {\n    r = 0.15,\n    g = 0.15,\n    b = 0.15\n}\n\nlocal lightningColor = {\n    r = 149/255,\n    g = 234/255,\n    b = 237/255\n}\n\nlocal mythicWireTransfers = {\n    -- First P1\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = false,\n        bottomRightLightning = true,\n        topRightLighting = false,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = true,\n        bottomRightLightning = false,\n        topRightLighting = true,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = false,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n    \n    -- Second P1\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = true,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = false,\n        bottomRightLightning = false,\n        topRightLighting = true,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = false,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n    \n    -- Third P1\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = false,\n        bottomRightLightning = false,\n        topRightLighting = true,\n    },\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = true,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = false,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n}\n\nlocal heroicWireTransfers = {\n    -- First P1\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = false,\n        bottomRightLightning = true,\n        topRightLighting = false,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = true,\n        bottomRightLightning = false,\n        topRightLighting = true,\n    },\n    \n    -- Second P1\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = true,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = false,\n        bottomRightLightning = true,\n        topRightLighting = true,\n    },\n    \n    -- Third P1\n    {\n        bottomLeftLightning = true,\n        topLeftLightning = false,\n        bottomRightLightning = true,\n        topRightLighting = false,\n    },\n    {\n        bottomLeftLightning = false,\n        topLeftLightning = false,\n        bottomRightLightning = false,\n        topRightLighting = false,\n    },\n}\n\nif not aura_env.region.mapTextures then\n    aura_env.region.mapTextures = {\n        background = aura_env.region:CreateTexture(nil, \"BACKGROUND\"),\n        \n        leftConveyor = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        middleConveyor = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        rightConveyor = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        \n        smallLeftConveyor = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        smallRightConveyor = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        \n        topLeftLightning = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        topRightLighting = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        bottomLeftLightning = aura_env.region:CreateTexture(nil, \"BORDER\"),\n        bottomRightLightning = aura_env.region:CreateTexture(nil, \"BORDER\"),\n    }\nend\n\nlocal mapTextures = aura_env.region.mapTextures\n\nmapTextures.background:SetColorTexture(backgroundColor.r, backgroundColor.g, backgroundColor.b, 1)\nmapTextures.background:SetAllPoints()\n\nmapTextures.leftConveyor:SetColorTexture(conveyorColor.r, conveyorColor.g, conveyorColor.b, 1)\nmapTextures.leftConveyor:SetPoint(\"TOPLEFT\", aura_env.region, \"TOPLEFT\")\nmapTextures.leftConveyor:SetPoint(\"BOTTOMRIGHT\", aura_env.region, \"BOTTOMLEFT\", conveyorWidth, 0)\n\nmapTextures.middleConveyor:SetColorTexture(conveyorColor.r, conveyorColor.g, conveyorColor.b, 1)\nmapTextures.middleConveyor:SetPoint(\"TOPRIGHT\", aura_env.region, \"TOPRIGHT\")\nmapTextures.middleConveyor:SetPoint(\"BOTTOMLEFT\", aura_env.region, \"BOTTOMRIGHT\", -conveyorWidth, 0)\n\nmapTextures.rightConveyor:SetColorTexture(conveyorColor.r, conveyorColor.g, conveyorColor.b, 1)\nmapTextures.rightConveyor:SetPoint(\"TOPLEFT\", aura_env.region, \"TOP\", -conveyorWidth / 2, 0)\nmapTextures.rightConveyor:SetPoint(\"BOTTOMRIGHT\", aura_env.region, \"BOTTOM\", conveyorWidth / 2, 0)\n\nmapTextures.smallLeftConveyor:SetColorTexture(conveyorColor.r, conveyorColor.g, conveyorColor.b, 1)\nmapTextures.smallLeftConveyor:SetPoint(\"TOPLEFT\", aura_env.region, \"LEFT\", 1.5 * conveyorWidth, 0.5 * conveyorWidth)\nmapTextures.smallLeftConveyor:SetPoint(\"BOTTOMRIGHT\", aura_env.region, \"CENTER\", -conveyorWidth, -0.5 * conveyorWidth)\n\nmapTextures.smallRightConveyor:SetColorTexture(conveyorColor.r, conveyorColor.g, conveyorColor.b, 1)\nmapTextures.smallRightConveyor:SetPoint(\"TOPRIGHT\", aura_env.region, \"RIGHT\", -1.5 * conveyorWidth, 0.5 * conveyorWidth)\nmapTextures.smallRightConveyor:SetPoint(\"BOTTOMLEFT\", aura_env.region, \"CENTER\", conveyorWidth, -0.5 * conveyorWidth)\n\nmapTextures.topLeftLightning:SetColorTexture(lightningColor.r, lightningColor.g, lightningColor.b, 1)\nmapTextures.topLeftLightning:SetPoint(\"TOPLEFT\", aura_env.region, \"TOPLEFT\", conveyorWidth, 0)\nmapTextures.topLeftLightning:SetPoint(\"BOTTOMRIGHT\", aura_env.region, \"CENTER\", - 0.5 * conveyorWidth, 0.5 * conveyorWidth)\n\nmapTextures.topRightLighting:SetColorTexture(lightningColor.r, lightningColor.g, lightningColor.b, 1)\nmapTextures.topRightLighting:SetPoint(\"TOPRIGHT\", aura_env.region, \"TOPRIGHT\", -conveyorWidth, 0)\nmapTextures.topRightLighting:SetPoint(\"BOTTOMLEFT\", aura_env.region, \"CENTER\", 0.5 * conveyorWidth, 0.5 * conveyorWidth)\n\nmapTextures.bottomLeftLightning:SetColorTexture(lightningColor.r, lightningColor.g, lightningColor.b, 1)\nmapTextures.bottomLeftLightning:SetPoint(\"BOTTOMLEFT\", aura_env.region, \"BOTTOMLEFT\", conveyorWidth, 0)\nmapTextures.bottomLeftLightning:SetPoint(\"TOPRIGHT\", aura_env.region, \"CENTER\", -0.5 * conveyorWidth, -0.5 * conveyorWidth)\n\nmapTextures.bottomRightLightning:SetColorTexture(lightningColor.r, lightningColor.g, lightningColor.b, 1)\nmapTextures.bottomRightLightning:SetPoint(\"BOTTOMRIGHT\", aura_env.region, \"BOTTOMRIGHT\", -conveyorWidth, 0)\nmapTextures.bottomRightLightning:SetPoint(\"TOPLEFT\", aura_env.region, \"CENTER\", 0.5 * conveyorWidth, -0.5 * conveyorWidth)\n\nfunction aura_env.ShowLightning(set, mythic)\n    local t = mythic and mythicWireTransfers or heroicWireTransfers\n    local wireTransfers = t[set]\n    \n    for texName, shown in pairs(wireTransfers) do\n        mapTextures[texName]:SetShown(shown)\n    end\nend\n\naura_env.ShowLightning(1, false)",
