["use_spellId"] = true,
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 5,\n            expirationTime = GetTime() + 5,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 5,\n            expirationTime = GetTime() + 5,\n            autoHide = true\n        }\n        \n        aura_env.lastStart = GetTime()\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, _, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 461060 and aura_env.coil then -- Spin To Win!\n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        elseif subEvent == \"SPELL_DAMAGE\" and spellID == 474665 then -- Coin Magnet\n            local state = allstates[\"\"]\n            local currentTime = GetTime()\n            \n            if currentTime < aura_env.lastStart + 5.2 then return end\n            \n            if not state then\n                aura_env.lastStart = currentTime - 1\n                \n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = 4,\n                    expirationTime = GetTime() + 4,\n                    autoHide = true\n                }\n                \n                return true\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 464809 then -- Reward: Coin and Shock\n            aura_env.coil = true\n            \n            C_Timer.After(1.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id) end)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.coil = false -- Whether the Premium Currency Coil is down\n        aura_env.lastStart = 0 -- Time at which the coil was last activated by a player\n    end\nend",
