["custom"] = "local letterToTokenName = {\n    B = \"Bomb\",\n    C = \"Coin\",\n    F = \"Flame\",\n    S = \"Shock\",\n}\n\nlocal originalTokenOrder = {\n    FS = \"SF\", -- Reward: Shock and Flame\n    BS = \"SB\", -- Reward: Shock and Bomb\n    BF = \"FB\", -- Reward: Flame and Bomb\n    CF = \"FC\", -- Reward: Flame and Coin\n    CS = \"CS\", -- Reward: Coin and Shock\n    BC = \"CB\", -- Reward: Coin and Bomb\n}\n\nlocal sortedTokenOrder = {\n    FS = \"FS\", -- Reward: Shock and Flame\n    SF = \"FS\", -- Reward: Shock and Flame\n    \n    BS = \"BS\", -- Reward: Shock and Bomb\n    SB = \"BS\", -- Reward: Shock and Bomb\n    \n    BF = \"BF\", -- Reward: Flame and Bomb\n    FB = \"BF\", -- Reward: Flame and Bomb\n    \n    CF = \"CF\", -- Reward: Flame and Coin\n    FC = \"CF\", -- Reward: Flame and Coin\n    \n    CS = \"CS\", -- Reward: Coin and Shock\n    SC = \"CS\", -- Reward: Coin and Shock\n    \n    BC = \"BC\", -- Reward: Coin and Bomb\n    CB = \"BC\", -- Reward: Coin and Bomb\n}\n\n-- If no MRT note priority is supplied, use this as the priority\nlocal defaultTokenPriorities = {\n    \"CF\", -- Flame and Coin\n    \"CS\", -- Coin and Shock\n    \"FS\", -- Shock and Flame\n    \"BF\", -- Flame and Bomb\n    \"BS\", -- Shock and Bomb\n    \"BC\", -- Coin and Bomb\n}\n\n-- Spell IDs of the Reel Assistant buffs, and their corresponding token\naura_env.spellIDToToken = {\n    [464484] = \"B\", -- Bomb Symbol\n    [464482] = \"C\", -- Coin Symbol\n    [464475] = \"F\", -- Flame Symbol\n    [464476] = \"S\", -- Shock Symbol\n}\n\n-- Spell IDs of the boss' reward casts, and their corresponding token combination\naura_env.spellIDToTokenCombination = {\n    [464772] = \"FS\", -- Reward: Shock and Flame\n    [464801] = \"BS\", -- Reward: Shock and Bomb\n    [464804] = \"BF\", -- Reward: Flame and Bomb\n    [464806] = \"CF\", -- Reward: Flame and Coin\n    [464809] = \"CS\", -- Reward: Coin and Shock\n    [464810] = \"BC\", -- Reward: Coin and Bomb\n}\n\n-- Takes in an array of 3 tokens\n-- Returns a table of 3 possible combinations you can make using those tokens\nlocal function GetAvailableTokenCombinations(availableTokens)\n    -- Sort the available tokens alphabetically\n    -- This is important for checking the combinations since e.g. FC and CF are the same\n    table.sort(availableTokens)\n    \n    -- Determine the available combinations\n    local availableTokenCombinations = {}\n    \n    availableTokenCombinations[string.format(\"%s%s\", availableTokens[1], availableTokens[2])] = true\n    availableTokenCombinations[string.format(\"%s%s\", availableTokens[1], availableTokens[3])] = true\n    availableTokenCombinations[string.format(\"%s%s\", availableTokens[2], availableTokens[3])] = true\n    \n    return availableTokenCombinations\nend\n\n-- Returns a list of token priorities, structued as <[token combination] = priority>\n-- Uses MRT note if supplied. Otherwise uses the default priority list.\nlocal function GetTokenPriorities()\n    local tokenPriorities = {}\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment() do\n        line = line:upper()\n        \n        for tokenCombination in string.gmatch(line, \"%S+\") do\n            tokenCombination = sortedTokenOrder[tokenCombination]\n            \n            -- Check if this is a valid token combination (correct letters, in either order)\n            -- And whether this is the first time it's been listed (ignore subsequent times)\n            local validTokenCombination = tokenCombination and not tContains(tokenPriorities, tokenCombination)\n            \n            if validTokenCombination then\n                table.insert(tokenPriorities, tokenCombination)\n            end\n        end\n    end\n    \n    -- Check if any token combination was not mentioned in the note\n    -- If so, put it in\n    for _, tokenCombination in ipairs(defaultTokenPriorities) do\n        if not tContains(tokenPriorities, tokenCombination) then\n            table.insert(tokenPriorities, tokenCombination)\n        end\n    end\n    \n    return tInvert(tokenPriorities)\nend\n\n-- Creates a state for all 6 possible token combinations\n-- They are indexed according to their priority\nfunction aura_env.CreateInitialStates(allstates)\n    local tokenPriorities = GetTokenPriorities()\n    \n    for combination, priority in pairs(tokenPriorities) do\n        local originalOrder = originalTokenOrder[combination]\n        local firstToken, secondToken = originalOrder:match(\"(%a)(%a)\")\n        \n        local firstTokenName = letterToTokenName[firstToken]\n        local secondTokenName = letterToTokenName[secondToken]\n        \n        allstates[combination] = {\n            show = true,\n            changed = true,\n            token1 = firstToken,\n            token2 = secondToken,\n            token1Name = firstTokenName,\n            token2Name = secondTokenName,\n            previouslySelected = false,\n            index = priority\n        }\n    end\nend\n\nfunction aura_env.HighlightAvailableTokenCombinations(allstates, availableTokens)\n    local availableTokenCombinations = GetAvailableTokenCombinations(availableTokens)\n    \n    for combination in pairs(availableTokenCombinations) do\n        local state = allstates[combination]\n        \n        state.available = true\n        state.changed = true\n    end\n    \n    -- Send out event for the token combination we want to do next\n    local bestIndex = 9999\n    local bestCombo = {}\n    \n    for _, state in pairs(allstates) do\n        if state.available and state.index < bestIndex then\n            bestIndex = state.index\n            \n            bestCombo = {\n                [state.token1] = true,\n                [state.token2] = true\n            }\n        end\n    end\n    \n    WeakAuras.ScanEvents(\"LIQUID_REEL_ASSISTANT_ASSIGNMENT\", bestCombo)\nend",
