["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local tokens = {\"B\", \"C\", \"F\", \"S\"}\n        local randomTokens = {}\n        \n        for _ = 1, 3 do\n            local randomIndex = math.random(#tokens)\n            local randomToken = tokens[randomIndex]\n            \n            table.insert(randomTokens, randomToken)\n            table.remove(tokens, randomIndex)\n        end\n        \n        aura_env.CreateInitialStates(allstates)\n        aura_env.HighlightAvailableTokenCombinations(allstates, randomTokens)\n        \n        -- Gray out one or two combinations\n        for _ = 1, 2 do\n            local randomIndex = math.random(6)\n            \n            for _, state in pairs(allstates) do\n                if state.index == randomIndex then\n                    state.index = state.index + 10\n                    state.previouslySelected = true\n                    state.changed = true\n                end\n            end\n        end\n    elseif event == \"FRAME_UPDATE\" then\n        if not aura_env.last or aura_env.last < GetTime() - 1 then\n            aura_env.last = GetTime()\n            \n            local availableTokens = {}\n            \n            for i = 1, 8 do\n                local unit = \"boss\" .. i\n                \n                if UnitExists(unit) then\n                    local token = WA_GetUnitAura(unit, 464484) and \"B\" or WA_GetUnitAura(unit, 464482) and \"C\"  or WA_GetUnitAura(unit, 464475) and \"F\"  or WA_GetUnitAura(unit, 464476) and \"S\" \n                    \n                    if token then\n                        table.insert(availableTokens, token)\n                    end\n                end\n            end\n            \n            if #availableTokens == 3 then\n                aura_env.HighlightAvailableTokenCombinations(allstates, availableTokens)\n                \n                return true\n            end\n        end\n    elseif event == \"TRIGGER\" then\n        local _, triggerStates = ...\n        \n        if not triggerStates then return end\n        \n        local availableTokens = {}\n        \n        for _, state in pairs(triggerStates) do\n            local spellID = state.spellId\n            local token = spellID and aura_env.spellIDToToken[spellID]\n            \n            if token then\n                table.insert(availableTokens, token)\n            end\n        end\n        \n        if #availableTokens == 3 then\n            aura_env.HighlightAvailableTokenCombinations(allstates, availableTokens)\n            \n            return true\n        end\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        local insertedCombination = spellID and aura_env.spellIDToTokenCombination[spellID]\n        \n        if not insertedCombination then return end\n        \n        -- Hide all highlights\n        for _, state in pairs(allstates) do\n            state.available = false\n            state.changed = true\n        end\n        \n        -- Gray out chosen combination\n        local state = allstates[insertedCombination]\n        \n        state.index = state.index + 10\n        state.previouslySelected = true\n        state.changed = true\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.CreateInitialStates(allstates)\n        \n        return true\n    end\nend",
