["init"] = {
["custom"] = "aura_env.spellIDToToken = {\n    [464484] = \"Bomb\", -- Bomb Symbol\n    [464482] = \"Coin\", -- Coin Symbol\n    [464475] = \"Flame\", -- Flame Symbol\n    [464476] = \"Shock\", -- Shock Symbol\n}\n\naura_env.holdSpellIDs = {\n    [472828] = \"Flame\",\n    [472783] = \"Shock\",\n    [472832] = \"Coin\",\n    [472837] = \"Bomb\"\n}\n\naura_env.insertSpellIDs = {\n    [472886] = \"Flame\",\n    [472882] = \"Shock\",\n    [472889] = \"Coin\",\n    [472868] = \"Bomb\"\n}\n\naura_env.tokenToIconID = {\n    Flame = 524795,\n    Shock = 136099,\n    Coin = 4638725,\n    Bomb = 133710\n}",
["do_custom"] = true,
