["icon"] = true,
["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["custom_hide"] = "timed",
["customVariables"] = "{\n    token = {\n        display = \"Token\",\n        type = \"select\",\n        values = {\n            Flame = \"Flame\",\n            Shock = \"Shock\",\n            Coin = \"Coin\",\n            Bomb = \"Bomb\"\n        }\n    }\n}",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["events"] = "CLEU:SPELL_AURA_APPLIED:SPELL_AURA_REMOVED:SPELL_CAST_SUCCESS:UNIT_DIED, ENCOUNTER_START, TRIGGER:2:3, UNIT_SPELLCAST_START:boss",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local tokens = {\"Bomb\", \"Coin\", \"Flame\", \"Shock\"}\n        local randomTokens = {}\n        \n        for _ = 1, 3 do\n            local randomIndex = math.random(#tokens)\n            local randomToken = tokens[randomIndex]\n            \n            table.insert(randomTokens, randomToken)\n            table.remove(tokens, randomIndex)\n        end\n        \n        for _, token in ipairs(randomTokens) do\n            local value = 100000 + math.random(900000)\n            local total = 1000000\n            \n            allstates[token] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = value,\n                total = total,\n                leftText = token,\n                rightText = string.format(\"%.2f%%\", (value / total) * 100),\n                icon = aura_env.tokenToIconID[token],\n                token = token,\n                index = token,\n                autoHide = false\n            }\n        end\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 461060 then -- Spin To Win!\n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            local token = aura_env.holdSpellIDs[spellID]\n            \n            if not token then return end\n            \n            local unit = aura_env.GUIDToUnit[destGUID]\n            \n            allstates[token] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = 1,\n                total = 1,\n                icon = aura_env.tokenToIconID[token],\n                leftText = token,\n                rightText = WA_ClassColorName(unit),\n                token = token,\n                index = token,\n                autoHide = false\n            }\n            \n            return true\n        elseif subEvent == \"SPELL_AURA_REMOVED\" then\n            if spellID == 461060 then -- Spin To Win!\n                for _, state in pairs(allstates) do\n                    state.show = false\n                    state.changed = true\n                end\n                \n                return true\n            else\n                local token = aura_env.holdSpellIDs[spellID]\n                \n                if not token then return end\n                \n                allstates[token] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"static\",\n                    value = 1,\n                    total = 1,\n                    icon = aura_env.tokenToIconID[token],\n                    leftText = token,\n                    rightText = \"Floor\",\n                    token = token,\n                    index = token,\n                    autoHide = false\n                }\n                \n                return true\n            end\n        elseif subEvent == \"UNIT_DIED\" then\n            local token = aura_env.GUIDToToken[destGUID]\n            \n            if not token then return end\n            \n            allstates[token] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = 1,\n                total = 1,\n                icon = aura_env.tokenToIconID[token],\n                leftText = token,\n                rightText = \"Floor\",\n                token = token,\n                index = token,\n                autoHide = false\n            }\n            \n            return true\n        elseif subEvent == \"SPELL_CAST_SUCCESS\" then\n            local token = aura_env.insertSpellIDs[spellID]\n            \n            if not token then return end\n            \n            local state = allstates[token]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"TRIGGER\" then\n        local triggerNum, triggerStates = ...\n        \n        if triggerNum == 2 then\n            for _, state in pairs(allstates) do\n                if state.unit and not UnitExists(state.unit) then\n                    state.show = false\n                    state.changed = true\n                end\n            end\n            \n            for _, triggerState in pairs(triggerStates) do\n                local unit = triggerState.unit\n                local GUID = UnitGUID(unit)\n                local token = aura_env.spellIDToToken[triggerState.spellId]\n                \n                local value = UnitHealth(unit)\n                local total = UnitHealthMax(unit)\n                \n                allstates[unit] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"static\",\n                    value = value,\n                    total = total,\n                    leftText = token,\n                    rightText = string.format(\"%.2f%%\", (value / total) * 100),\n                    icon = aura_env.tokenToIconID[token],\n                    token = token,\n                    index = token,\n                    unit = unit,\n                    autoHide = false\n                }\n                \n                aura_env.GUIDToToken[GUID] = token\n            end\n        elseif triggerNum == 3 then\n            for _, triggerState in pairs(triggerStates) do\n                local unit = triggerState.unit\n                local state = allstates[unit]\n                \n                if state then\n                    state.value = triggerState.value\n                    state.total = triggerState.total\n                    state.rightText = string.format(\"%.2f%%\", triggerState.percenthealth or 100)\n                    state.changed = true\n                end\n            end\n        end\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.GUIDToToken = {} -- Reel Assistant GUIDs -> token they are holding\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n    end\nend",
