["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local assignments = {\n            \"|cFF226FFFShock|r & |cFFFF6B29Flame|r\",\n            \"|cFF226FFFShock|r & |cFF7B8585Bomb|r\",\n            \"|cFFFF6B29Flame|r & |cFF7B8585Bomb|r\",\n            \"|cFFFF6B29Flame|r & |cFFFFD429Coin|r\",\n            \"|cFFFFD429Coin|r & |cFF226FFFShock|r\",\n            \"|cFFFFD429Coin|r & |cFF7B8585Bomb|r\",\n        }\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            assignment = assignments[math.random(#assignments)],\n            autoHide = true\n        }\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 461060 then -- Spin To Win!\n            aura_env.count = aura_env.count + 1\n            \n            local assignment = aura_env.order[aura_env.count]\n            \n            if assignment then\n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = 4,\n                    expirationTime = GetTime() + 4,\n                    assignment = assignment,\n                    autoHide = true\n                }\n                \n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 0\n        aura_env.order = aura_env.GetTokenOrder()\n    end\nend\n\n\n\n",
