["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["custom_hide"] = "timed",
["customVariables"] = "{\n    macroPressed = {\n        display = \"Macro pressed\",\n        type = \"bool\"\n    }\n}",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["events"] = "ENCOUNTER_START, LIQUID_ASSIGNMENT_EVENT, LIQUID_PRIVATE_AURA_MACRO, CLEU:SPELL_AURA_APPLIED:UNIT_DIED:SPELL_AURA_REMOVED:SPELL_DISPEL, LIQUID_DISPEL_EVENT",
["custom"] = "function(allstates, event, ...)\n    if event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local healerOrder = CopyTable(aura_env.healerOrder)\n        local healerPriority = tInvert(healerOrder)\n        \n        -- Clear states\n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        \n        -- Count active assignments per healer\n        local assignmentCount = {}\n        \n        for _, assignment in ipairs(aura_env.assignments) do\n            local GUID = assignment.healerGUID\n            \n            if GUID then\n                assignmentCount[GUID] = (assignmentCount[GUID] or 0) + 1\n            end\n        end\n        \n        -- Assign healers\n        for _, assignment in ipairs(aura_env.assignments) do\n            local targetGUID = assignment.GUID\n            \n            if not assignment.healerGUID then\n                -- If the player that needs to be dispelled is a healer, and they are not yet assigned to dispel anyone else, self dispel\n                -- This automatically checks if the healer is alive, because if they weren't, their assignment would have been removed already\n                if tContains(healerOrder, targetGUID) and not assignmentCount[targetGUID] then\n                    assignment.healerGUID = targetGUID\n                    assignmentCount[targetGUID] = 1\n                    aura_env.lastAssignmentTime[targetGUID] = GetTime()\n                else -- If not, assign based on priority\n                    local currentTime = GetTime()\n                    \n                    table.sort(\n                        healerOrder,\n                        function(guidA, guidB)\n                            local priorityA = healerPriority[guidA] or 100\n                            local priorityB = healerPriority[guidB] or 100\n                            \n                            local lastDispelTimeA = aura_env.dispelTimes[guidA] or 0\n                            local lastDispelTimeB = aura_env.dispelTimes[guidB] or 0\n                            \n                            local dispelAvailableA = lastDispelTimeA < currentTime - 8\n                            local dispelAvailableB = lastDispelTimeB < currentTime - 8\n                            \n                            local unitA = aura_env.GUIDToUnit[guidA]\n                            local unitB = aura_env.GUIDToUnit[guidB]\n                            \n                            local deadA = UnitIsDeadOrGhost(unitA)\n                            local deadB = UnitIsDeadOrGhost(unitB)\n                            \n                            local countA = assignmentCount[guidA] or 0\n                            local countB = assignmentCount[guidB] or 0\n                            \n                            local lastAssignmentTimeA = aura_env.lastAssignmentTime[guidA]\n                            local lastAssignmentTimeB = aura_env.lastAssignmentTime[guidB]\n                            \n                            if deadA ~= deadB then -- Assign alive players over dead players\n                                return deadB\n                            elseif countA ~= countB then -- Assign not already assigned player over assigned players\n                                return countA < countB\n                            elseif dispelAvailableA ~= dispelAvailableB then -- Assign players with a dispel available over those that don't\n                                return dispelAvailableA\n                            elseif priorityA ~= priorityB then -- Assign based on priorioty\n                                return priorityA < priorityB\n                            elseif lastAssignmentTimeA ~= lastAssignmentTimeB then\n                                return lastAssignmentTimeA < lastAssignmentTimeB\n                            else\n                                return guidA < guidB\n                            end\n                        end\n                    )\n                    \n                    local assignedHealerGUID = healerOrder[1]\n                    \n                    assignment.healerGUID = assignedHealerGUID\n                    assignmentCount[assignedHealerGUID] = (assignmentCount[assignedHealerGUID] or 0) + 1\n                    aura_env.lastAssignmentTime[assignedHealerGUID] = GetTime()\n                end\n            end\n        end\n        \n        -- Glow frames\n        for _, assignment in ipairs(aura_env.assignments) do\n            if assignment.healerGUID == WeakAuras.myGUID then\n                local targetGUID = assignment.GUID\n                local macroPressed = assignment.macroPressed\n                local unit = aura_env.GUIDToUnit[targetGUID]\n                \n                allstates[targetGUID .. tostring(macroPressed)] = {\n                    show = true,\n                    changed = true,\n                    macroPressed = macroPressed,\n                    unit = unit\n                }\n                \n                if macroPressed and aura_env.config.tts and not aura_env.ttsPlayed[targetGUID] then\n                    LiquidWeakAuras:PlaySound(\"dispel\")\n                    \n                    aura_env.ttsPlayed[targetGUID] = true\n                end\n            end\n        end\n        \n        return true\n    elseif event == \"LIQUID_PRIVATE_AURA_MACRO\" then\n        if C_UnitAuras.GetPlayerAuraBySpellID(aura_env.spellID) and not aura_env.macroPressed then\n            aura_env.macroPressed = true\n            \n            SendChatMessage(\"Withering Flame macro pressed!\", \"RAID\")\n            \n            LiquidWeakAuras:Broadcast(\"LIQUID_DISPEL_EVENT\", aura_env.spellID, WeakAuras.myGUID)\n        end\n    elseif event == \"LIQUID_DISPEL_EVENT\" then\n        local spellID, GUID = ...\n        \n        if spellID ~= aura_env.spellID then return end\n        \n        for _, assignment in ipairs(aura_env.assignments) do\n            if assignment.GUID == GUID then\n                assignment.macroPressed = true\n            end\n        end\n        \n        WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, sourceGUID, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == aura_env.spellID then\n            table.insert(\n                aura_env.assignments,\n                {\n                    GUID = destGUID,\n                    macroPressed = false\n                }\n            )\n            \n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellID == aura_env.spellID then\n            aura_env.ttsPlayed[destGUID] = nil\n            \n            if destGUID == WeakAuras.myGUID then\n                aura_env.macroPressed = false\n            end\n            \n            for i, assignment in ipairs(aura_env.assignments) do\n                if destGUID == assignment.GUID then\n                    table.remove(aura_env.assignments, i)\n                    \n                    break\n                end\n            end\n            \n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        elseif subEvent == \"UNIT_DIED\" then\n            for _, assignment in ipairs(aura_env.assignments) do\n                if destGUID == assignment.healerGUID then\n                    assignment.healerGUID = nil\n                end\n            end\n            \n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        elseif subEvent == \"SPELL_DISPEL\" then\n            aura_env.dispelTimes[sourceGUID] = GetTime()\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.macroPressed = false -- Whether we pressed our macro\n        aura_env.assignments = {} -- {{healerGUID = <GUID>, GUID = <GUID>, macroPressed = <bool>}}\n        aura_env.healerOrder = {} -- Array of healer GUIDs, either in order of the note (if supplied) or sorted by GUID\n        aura_env.lastAssignmentTime = {} -- {healerGUID -> time}\n        aura_env.ttsPlayed = {} -- {GUID -> ttsPlayed} this resets on SPELL_AURA_REMOVED\n        aura_env.GUIDToUnit = {}\n        aura_env.dispelTimes = {} -- GUID to when they last dispelled\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n        \n        for _, line in LiquidWeakAuras:IterateNoteAssignment(2) do\n            aura_env.healerOrder = LiquidWeakAuras:LineToGUIDArray(line)\n            \n            if next(aura_env.healerOrder) then\n                for _, GUID in ipairs(aura_env.healerOrder) do\n                    aura_env.lastAssignmentTime[GUID] = 0\n                end\n                \n                break\n            end\n        end\n        \n        -- If no note is supplied, order healers based on GUID\n        if not next(aura_env.healerOrder) then\n            for unit in WA_IterateGroupMembers() do\n                local GUID = UnitGUID(unit)\n                \n                if UnitGroupRolesAssigned(unit) == \"HEALER\" then\n                    table.insert(aura_env.healerOrder, GUID)\n                    \n                    aura_env.lastAssignmentTime[GUID] = 0\n                end\n            end\n            \n            table.sort(aura_env.healerOrder)\n        end\n    end\nend",
