["custom"] = "function(allstates, event, ...)\n    if event == \"ENCOUNTER_START\" then\n        aura_env.GUIDs = {}\n    elseif event == \"NAME_PLATE_UNIT_ADDED\" and ... then\n        local unit = ...\n        local GUID = UnitGUID(unit)\n        \n        if aura_env.GUIDs[GUID] and C_UnitAuras.GetPlayerAuraBySpellID(465009) then -- Explosive Gaze\n            allstates[GUID] = {\n                show = true,\n                unit = unit,\n                changed = true\n            }\n            \n            return true\n        end\n    elseif event == \"NAME_PLATE_UNIT_REMOVED\" and ... then\n        local unit = ... \n        local GUID = UnitGUID(unit)\n        local state = allstates[GUID]\n        \n        if state and state.unit == unit then\n            state.show = false\n            state.changed = true\n            return true\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" and ... then\n        local _, subEvent, _, sourceGUID, _, _, _, destGUID, _, _, _, spellId = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellId == 465009 and destGUID == WeakAuras.myGUID then -- Explosive Gaze\n            aura_env.GUIDs[sourceGUID] = true\n            \n            for _, t in pairs(C_NamePlate.GetNamePlates()) do\n                local unit = t.namePlateUnitToken\n                \n                if UnitGUID(unit) == sourceGUID then\n                    allstates[sourceGUID] = {\n                        show = true,\n                        changed = true,\n                        unit = unit\n                    }\n                    \n                    return true\n                end\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellId == 465009 and destGUID == WeakAuras.myGUID then -- Explosive Gaze\n            aura_env.GUIDs[sourceGUID] = nil\n            local state = allstates[sourceGUID]\n            \n            if state then\n                state.changed = true\n                state.show = false\n                \n                return true\n            end\n        end\n    end\nend",
