["init"] = {
["custom"] = "local sortedTokenOrder = {\n    FS = \"FS\", -- Reward: Shock and Flame\n    SF = \"FS\", -- Reward: Shock and Flame\n    \n    BS = \"BS\", -- Reward: Shock and Bomb\n    SB = \"BS\", -- Reward: Shock and Bomb\n    \n    BF = \"BF\", -- Reward: Flame and Bomb\n    FB = \"BF\", -- Reward: Flame and Bomb\n    \n    CF = \"CF\", -- Reward: Flame and Coin\n    FC = \"CF\", -- Reward: Flame and Coin\n    \n    CS = \"CS\", -- Reward: Coin and Shock\n    SC = \"CS\", -- Reward: Coin and Shock\n    \n    BC = \"BC\", -- Reward: Coin and Bomb\n    CB = \"BC\", -- Reward: Coin and Bomb\n}\n\nlocal combinationToSpellIDs = {\n    FS = {[464475] = true, [464476] = true},\n    BS = {[464484] = true, [464476] = true},\n    BF = {[464484] = true, [464475] = true},\n    CF = {[464482] = true, [464475] = true},\n    CS = {[464482] = true, [464476] = true},\n    BC = {[464484] = true, [464482] = true}\n}\n\n-- Returns a list of token priorities, structured as <[token combination] = priority>\n-- Uses MRT note if supplied. Otherwise uses the default priority list.\nfunction aura_env.GetTokenOrder()\n    local tokenPriorities = {}\n    local debuffOrder = {}\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment() do\n        line = line:upper()\n        \n        for tokenCombination in string.gmatch(line, \"%S+\") do\n            tokenCombination = sortedTokenOrder[tokenCombination]\n            \n            -- Check if this is a valid token combination (correct letters, in either order)\n            -- And whether this is the first time it's been listed (ignore subsequent times)\n            local validTokenCombination = tokenCombination and not tContains(tokenPriorities, tokenCombination)\n            \n            if validTokenCombination then\n                table.insert(tokenPriorities, tokenCombination)\n            end\n        end\n    end\n    \n    if not next(tokenPriorities) then\n        tokenPriorities = {\n            \"CF\", \"CS\", \"FS\", \"BF\", \"BS\", \"BC\"\n        }\n    end\n    \n    for _, tokenCombination in ipairs(tokenPriorities) do\n        local spellIDs = combinationToSpellIDs[tokenCombination]\n        \n        table.insert(debuffOrder, spellIDs)\n    end\n    \n    return debuffOrder\nend",
