["events"] = "ENCOUNTER_START, UNIT_SPELLCAST_START:boss, NAME_PLATE_UNIT_ADDED, NAME_PLATE_UNIT_REMOVED",
["custom"] = "function(allstates, event, ...)\n    if event == \"NAME_PLATE_UNIT_ADDED\" then\n        local unit = ...\n        \n        if not unit then return end\n        if not aura_env.spellIDs then return end\n        \n        local hasCoin = false\n        \n        for spellID in pairs(aura_env.spellIDs) do\n            if WA_GetUnitAura(unit, spellID) then\n                hasCoin = true\n                \n                break\n            end\n        end\n        \n        if not hasCoin then return end\n        \n        allstates[unit] = {\n            show = true,\n            changed = true,\n            unit = unit\n        }\n        \n        return true\n    elseif event == \"NAME_PLATE_UNIT_REMOVED\" then\n        local unit = ...\n        \n        if not unit then return end\n        \n        local state = allstates[unit]\n        \n        if state then\n            state.show = false\n            state.changed = true\n            \n            return true\n        end\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 461060 then -- Spin To Win!\n            aura_env.count = aura_env.count + 1\n            aura_env.spellIDs = aura_env.order[aura_env.count]\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 0\n        aura_env.order = aura_env.GetTokenOrder()\n    end\nend",
