["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local spellIDs = {\n            464772,\n            464801,\n            464809\n        }\n        \n        local spellID = spellIDs[math.random(3)]\n        local spellInfo = C_Spell.GetSpellInfo(spellID)\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 3,\n            expirationTime = GetTime() + 3,\n            icon = spellInfo.iconID,\n            spellID = spellID,\n            autoHide = true\n        }\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local unit, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if not aura_env.spellIDs[spellID] then return end\n        if not UnitDetailedThreatSituation(\"player\", unit) then return end\n        \n        local spellInfo = C_Spell.GetSpellInfo(spellID)\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 3,\n            expirationTime = GetTime() + 3,\n            icon = spellInfo.iconID,\n            spellID = spellID,\n            autoHide = true\n        }\n        \n        return true\n    end\nend",
