["unit"] = "player",
["custom"] = "function(event, ...)\n    if event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" then\n        if not aura_env.config.enable then return end\n        \n        for i = 1, 8 do\n            local unit = \"boss\" .. i\n            \n            if UnitExists(unit) then\n                local GUID = UnitGUID(unit)\n                local newGUID = not aura_env.seenGUIDs[GUID]\n                \n                local npcID = select(6, strsplit(\"-\", GUID))\n                local isReelAssistant = npcID == \"228463\"\n                \n                if newGUID and isReelAssistant then\n                    aura_env.count = aura_env.count + 1\n                    \n                    local existingMark = GetRaidTargetIndex(unit)\n                    local mark = aura_env.marks[aura_env.count]\n                    \n                    if mark and existingMark ~= mark then\n                        SetRaidTarget(unit, mark)\n                    end\n                    \n                    aura_env.seenGUIDs[GUID] = true\n                end\n            end\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, _, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_REMOVED\" and spellID == 461060 then -- Spin To Win!\n            aura_env.count = 0\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.seenGUIDs = {}\n        aura_env.count = 0\n    end\nend",
