["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local playerClass = UnitClassBase(\"player\")\n        local abilityName = playerClass == \"PRIEST\" and \"Grip\" or \"Rescue\"\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 5.8,\n            expirationTime = GetTime() + 5.8,\n            assignment = string.format(\"%s %s\", abilityName, WA_ClassColorName(\"player\")),\n            autoHide = true\n        }\n    elseif event == \"LIQUID_GAOL_ASSIGN_ALL\" then\n        local gaolAssignments = ...\n        local inGaolGUIDs = {} -- GUIDs of all players assigned to a Gaol (affected or not): GUID = gaolNumber\n        local affected = {} -- GUIDs of players targeted with Gaol\n        local toGripGUIDs = {} -- Array of GUIDs that require a grip, in order of priority\n        local gripperGUIDs = {} -- Array of GUIDs available to grip, in order of MRT priority\n        local racialAssignments = {} -- GUIDs of players assigned to use Void Elf racial\n        local healerGaols = {} -- Gaols that have a healer in them\n        \n        -- Determine GUIDs of players assigned to Gaols (whether they are targeted or not)\n        -- Determine GUIDs of players targeted by a Gaols\n        for gaolNumber, GUIDs in ipairs(gaolAssignments) do\n            for _, GUID in ipairs(GUIDs) do\n                local unit = aura_env.GUIDToUnit[GUID]\n                local isAffected = WA_GetUnitDebuff(unit, 472631) -- Earthshaker Gaol\n                local role = UnitGroupRolesAssigned(unit)\n                \n                inGaolGUIDs[GUID] = gaolNumber\n                \n                if isAffected then\n                    affected[GUID] = true\n                end\n                \n                if role == \"HEALER\" then\n                    healerGaols[gaolNumber] = true\n                end\n            end\n        end\n        \n        -- Determine GUIDs that require a grip\n        for _, GUID in ipairs(aura_env.toGripGUIDs) do\n            local isAffected = affected[GUID]\n            \n            if isAffected then\n                table.insert(toGripGUIDs, GUID)\n            end\n        end\n        \n        -- Determine GUIDs that are available to grip\n        for _, GUID in ipairs(aura_env.gripperGUIDs) do\n            local unit = aura_env.GUIDToUnit[GUID]\n            local isAlive = not UnitIsDeadOrGhost(unit)\n            local isConnected = UnitIsConnected(unit)\n            local isVisible = UnitIsVisible(unit)\n            local inGaol = inGaolGUIDs[GUID] -- Players that are assigned to soak a Gaol themselves cannot rescue/grip\n            \n            if isAlive and isConnected and isVisible and not inGaol then\n                table.insert(gripperGUIDs, GUID)\n            end\n        end\n        \n        -- If there are more players that require grips than there are grippers available\n        -- First try to assign Void Elf racials\n        local excessCount = #toGripGUIDs - #gripperGUIDs\n        \n        if excessCount > 0 then\n            for i, GUID in ipairs_reverse(toGripGUIDs) do\n                local isVoidElf = aura_env.voidElfGUIDs[GUID]\n                local racialLastUsed = aura_env.voidElfRacialUseTimes[GUID] or 0\n                local racialAvailable = racialLastUsed < GetTime() - 180\n                \n                if isVoidElf and racialAvailable then\n                    racialAssignments[GUID] = true\n                    excessCount = excessCount - 1\n                    \n                    table.remove(toGripGUIDs, i)\n                    \n                    if excessCount == 0 then break end\n                end\n            end\n        end\n        \n        -- Next, eliminate GUIDs from being gripped that are in Gaols with a healer\n        if excessCount > 0 then\n            for i, GUID in ipairs_reverse(toGripGUIDs) do\n                local gaolNumber = inGaolGUIDs[GUID]\n                local healerGaol = healerGaols[gaolNumber]\n                \n                if healerGaol then\n                    excessCount = excessCount - 1\n                    \n                    table.remove(toGripGUIDs, i)\n                    \n                    if excessCount == 0 then break end\n                end\n            end\n        end\n        \n        -- Next, eliminate GUIDs (including those in Gaols with no healers)\n        if excessCount > 0 then\n            for i in ipairs_reverse(toGripGUIDs) do\n                excessCount = excessCount - 1\n                \n                table.remove(toGripGUIDs, i)\n                \n                if excessCount == 0 then break end\n            end\n        end\n        \n        -- Show states for grippers\n        for i, toGripGUID in ipairs(toGripGUIDs) do\n            local gripperGUID = gripperGUIDs[i]\n            \n            if gripperGUID == WeakAuras.myGUID then\n                local toGripUnit = aura_env.GUIDToUnit[toGripGUID]\n                local playerClass = UnitClassBase(\"player\")\n                local abilityName = playerClass == \"PRIEST\" and \"Grip\" or \"Rescue\"\n                \n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = 5.8,\n                    expirationTime = GetTime() + 5.8,\n                    assignment = string.format(\"%s %s\", abilityName, WA_ClassColorName(toGripUnit)),\n                    unit = toGripUnit,\n                    autoHide = true\n                }\n                \n                if aura_env.config.tts then\n                    LiquidWeakAuras:PlaySound(abilityName)\n                end\n            end\n        end\n        \n        -- Show states for players assigned to use their Void Elf racial\n        if racialAssignments[WeakAuras.myGUID] then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 5.8,\n                expirationTime = GetTime() + 5.8,\n                assignment = \"Void Elf racial\",\n                autoHide = true\n            }\n            \n            if aura_env.config.tts then\n                LiquidWeakAuras:PlaySound(\"Void Elf\")\n            end\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local unit, _, spellID = ...\n        \n        if spellID == 256948 then -- Spatial Rift\n            local GUID = UnitGUID(unit)\n            \n            aura_env.voidElfRacialUseTimes[GUID] = GetTime()\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.toGripGUIDs = {}\n        aura_env.gripperGUIDs = {}\n        aura_env.GUIDToUnit = {}\n        aura_env.voidElfGUIDs = {}\n        aura_env.voidElfRacialUseTimes = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            local _, race = UnitRace(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n            \n            if race == \"VoidElf\" then\n                aura_env.voidElfGUIDs[GUID] = true\n            end\n        end\n        \n        for lineCount, line in LiquidWeakAuras:IterateNoteAssignment(3) do\n            local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n            \n            if lineCount == 1 then\n                aura_env.toGripGUIDs = GUIDs\n            elseif lineCount == 2 then\n                -- Only add priests/evokers\n                for _, GUID in ipairs(GUIDs) do\n                    local unit = aura_env.GUIDToUnit[GUID]\n                    local class = UnitClassBase(unit)\n                    \n                    if class == \"PRIEST\" or class == \"EVOKER\" then\n                        table.insert(aura_env.gripperGUIDs, GUID)\n                    end\n                end\n            end\n        end\n    end\nend",
