["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = aura_env.duration - 0.2,\n            expirationTime = GetTime() + aura_env.duration - 0.2,\n            assignment = math.random(2) == 1 and \"Left\" or \"Right\",\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env.set = aura_env.set + 1\n        \n        if aura_env.set == 2 then\n            LiquidWeakAuras:SortTable(aura_env.affected)\n        else\n            LiquidWeakAuras:SortTable(aura_env.affected, true)\n        end\n        \n        local myAssignmentIndex = tIndexOf(aura_env.affected, WeakAuras.myGUID)\n        \n        if myAssignmentIndex then\n            local totalAssignments = #aura_env.affected\n            local assignment = myAssignmentIndex <= (totalAssignments / 2) and \"Left\" or \"Right\"\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = aura_env.duration - 0.2,\n                expirationTime = GetTime() + aura_env.duration - 0.2,\n                assignment = assignment,\n                autoHide = true\n            }\n            \n            if aura_env.config.tts then\n                LiquidWeakAuras:PlaySound(assignment)\n            end\n        end\n        \n        aura_env.affected = {}\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == aura_env.spellID then\n            table.insert(aura_env.affected, destGUID)\n            \n            if #aura_env.affected == 1 then\n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.affected = {}\n        aura_env.set = 0\n    end\nend",
