["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then return end\n    if not (aura_env.state and aura_env.state.say) then return end\n    \n    if not aura_env.last or aura_env.last < GetTime() - 1 then\n        aura_env.last = GetTime()\n        \n        SendChatMessage(aura_env.state.say)\n    end\nend",
["yOffset"] = 0,
["anchorPoint"] = "CENTER",
["customTextUpdate"] = "update",
["actions"] = {
["start"] = {
},
["init"] = {
["custom"] = "-- Nickname to gaol number per set\n-- These are only used if the player is not assigned to be in a gaol by default, and is affected\n-- Should only be one nickname per gaol per set (e.g. don't hard assign 2 nicknames to the same gaol)\nlocal hardAssignments = {\n    [1] = { -- 2 Gaols\n        \n    },\n    [2] = { -- 3 Gaols\n        \n    },\n    [3] = { -- 4 Gaols\n        Ksp = 1,\n        Drenaco = 1,\n        Imfiredup = 2\n    },\n    [4] = { -- 4 Gaols\n        Ksp = 1,\n        Drenaco = 1,\n        Imfiredup = 2\n    }\n}\n\nlocal duration = 5.8\nlocal hotMessPosition = {0, 0}\nlocal gaolPositions = {\n    [2] = {\n        {-30, -45},\n        {30, -45},\n    },\n    [3] = {\n        {-40, -15},\n        {-75, -40},\n        {40, -15},\n    },\n    [4] = {\n        {-22, -15},\n        {-70, -45},\n        {22, -15},\n        {57, -40},\n    }\n}\n\nlocal sayMessage = {\n    [2] = {\n        \"Left\",\n        \"Right\"\n    },\n    [3] = {\n        \"Front left\",\n        \"Back left\",\n        \"Right\"\n    },\n    [4] = {\n        \"Front left\",\n        \"Back left\",\n        \"Front right\",\n        \"Back right\",\n    }\n}\n\nlocal tts = {\n    [2] = {\n        \"left\",\n        \"right\"\n    },\n    [3] = {\n        \"front left\",\n        \"back left\",\n        \"right\"\n    },\n    [4] = {\n        \"front left\",\n        \"back left\",\n        \"front right\",\n        \"back right\",\n    }\n}\n\nfunction aura_env.ReadNoteAssignments()\n    aura_env.gaolAssignments = {}\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment() do\n        local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n        \n        if next(GUIDs) then\n            table.insert(aura_env.gaolAssignments, GUIDs)\n        end\n    end\n    \n    aura_env.note = next(aura_env.gaolAssignments) -- Whether MRT assignments were provided. Aura does nothing if not.\n    \n    local nicknameToGUID = {}\n    \n    for unit in WA_IterateGroupMembers() do\n        local GUID = UnitGUID(unit)\n        local nickname = LiquidWeakAuras:GetNickname(unit)\n        \n        if nickname then\n            nicknameToGUID[nickname] = GUID\n        end\n    end\n    \n    aura_env.hardAssignments = {}\n    \n    for set, nicknames in ipairs(hardAssignments) do\n        aura_env.hardAssignments[set] = {}\n        \n        for nickname, gaolNumber in pairs(nicknames) do\n            local GUID = nicknameToGUID[nickname]\n            \n            if GUID then\n                aura_env.hardAssignments[set][GUID] = gaolNumber\n            end\n        end\n    end\nend\n\n-- Creates states for Hot Mess and Gaols\nfunction aura_env.CreateDefaultStates(allstates, gaolCount)\n    -- Hot Mess\n    if gaolCount <= 3 then\n        allstates[1] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration,\n            expirationTime = GetTime() + duration,\n            type = \"HOT_MESS\",\n            index = 1,\n            position = hotMessPosition,\n            autoHide = true\n        }\n    end\n    \n    -- Gaols\n    for i = 1, gaolCount do\n        allstates[i + 1] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration,\n            expirationTime = GetTime() + duration,\n            type = \"GAOL\",\n            index = i + 1,\n            position = gaolPositions[gaolCount][i],\n            autoHide = true\n        }\n    end\nend\n\n-- Assigns the player to a Gaol\n-- Different: the gaol we got assigned is different than our preferred gaol\nfunction aura_env.Assign(allstates, gaolCount, gaolNumber, affected, different)\n    local position = gaolPositions[gaolCount] and gaolPositions[gaolCount][gaolNumber]\n    \n    if not position then return end\n    \n    allstates[6] = {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        duration = duration,\n        expirationTime = GetTime() + duration,\n        index = 6,\n        say = affected and sayMessage[gaolCount][gaolNumber],\n        position = position,\n        autoHide = true\n    }\n    \n    if aura_env.config.tts and not WeakAuras.IsOptionsOpen() then\n        LiquidWeakAuras:PlaySound(tts[gaolCount][gaolNumber])\n    end\n    \n    WeakAuras.ScanEvents(\"LIQUID_GAOL_ASSIGN\", sayMessage[gaolCount][gaolNumber], different)\nend",
