["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local gaolCount = 4\n        local gaolNumber = math.random(gaolCount)\n        \n        aura_env.CreateDefaultStates(allstates, gaolCount)\n        aura_env.Assign(allstates, gaolCount, gaolNumber)\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        if not aura_env.note then return end\n        \n        aura_env.set = aura_env.set + 1\n        \n        LiquidWeakAuras:SortTable(aura_env.affected)\n        \n        local gaolCount = #aura_env.affected\n        local gaolAssignments = {}\n        local playerAffected = tContains(aura_env.affected, WeakAuras.myGUID)\n        local hardAssignments = aura_env.hardAssignments[aura_env.set] -- GUID to gaol number\n        local playerPreferredGaol\n        \n        if aura_env.set == 1 then\n            gaolAssignments[1] = aura_env.gaolAssignments[1]\n            gaolAssignments[2] = aura_env.gaolAssignments[2]\n            \n            if #gaolAssignments < 2 then return end\n        elseif aura_env.set == 2 then\n            gaolAssignments[1] = aura_env.gaolAssignments[3]\n            gaolAssignments[2] = aura_env.gaolAssignments[4]\n            gaolAssignments[3] = aura_env.gaolAssignments[5]\n            \n            if #gaolAssignments < 3 then return end\n        else\n            gaolAssignments[1] = aura_env.gaolAssignments[6]\n            gaolAssignments[2] = aura_env.gaolAssignments[7]\n            gaolAssignments[3] = aura_env.gaolAssignments[8]\n            gaolAssignments[4] = aura_env.gaolAssignments[9]\n            \n            if #gaolAssignments < 4 then return end\n        end\n        \n        -- GUIDs of player that are part of regular assignments\n        -- Hard assignments for these GUIDs are ignored (since they are already part of a gaol set)\n        local regularAssignedGUIDs = {}\n        \n        for _, gaolGUIDs in ipairs(gaolAssignments) do\n            for _, GUID in ipairs(gaolGUIDs) do\n                regularAssignedGUIDs[GUID] = true\n            end\n        end\n        \n        -- Find our preferred assignment\n        -- This is just used for display purposes: if we are assigned to a different gaol than our preferred one, the text is red\n        for gaolNumber, gaolGUIDs in ipairs(gaolAssignments) do\n            if tContains(gaolGUIDs, WeakAuras.myGUID) then\n                playerPreferredGaol = gaolNumber\n                \n                break\n            end\n        end\n        \n        -- Gaol numbers that have an affected GUID in them\n        -- When attempting to swap players (because there are multiple gaols on the same assignment), only swap with empty gaols\n        local hasAffectedGUID = {}\n        \n        for gaolNumber, gaolGUIDs in ipairs(gaolAssignments) do\n            for _, GUID in ipairs(gaolGUIDs) do\n                if tContains(aura_env.affected, GUID) then\n                    hasAffectedGUID[gaolNumber] = true\n                    \n                    break\n                end\n            end\n        end\n        \n        -- Place hard assigned players (that are not in the regular assignments) in index 1 for their gaol\n        -- This should be safe since there is a maximum of one player hard assigned per Gaol\n        local sortedHardAssignments = {}\n        \n        for GUID in pairs(hardAssignments) do\n            table.insert(sortedHardAssignments, GUID)\n        end\n        \n        LiquidWeakAuras:SortTable(sortedHardAssignments)\n        \n        for _, GUID in ipairs(sortedHardAssignments) do\n            local preferredGaolNumber = hardAssignments[GUID]\n            \n            if not regularAssignedGUIDs[GUID] and tContains(aura_env.affected, GUID) then\n                if #gaolAssignments[preferredGaolNumber] >= 2 then\n                    table.insert(gaolAssignments[preferredGaolNumber], 3, GUID)\n                else\n                    table.insert(gaolAssignments[preferredGaolNumber], GUID)\n                end\n                \n                hasAffectedGUID[preferredGaolNumber] = true\n            end\n        end\n        \n        for gaolNumber, gaolGUIDs in ipairs(gaolAssignments) do\n            local affectedPlayers = 0\n            \n            for guidNumber, GUID in ipairs(gaolGUIDs) do\n                if tContains(aura_env.affected, GUID) then\n                    affectedPlayers = affectedPlayers + 1\n                    \n                    -- If there's more than one affected player in this Gaol, swap it with someone from the other Gaol\n                    -- Prefer players that have the same index on the note\n                    if affectedPlayers > 1 then\n                        local swapped = false\n                        \n                        -- Try to swap with a gaol that actually has someone on the same index (over one that has fewer players than the index)\n                        for otherGaolNumber, otherGaolGUIDs in ipairs(gaolAssignments) do\n                            -- Select a Gaol that doesn't have an affected GUID in it\n                            if gaolNumber ~= otherGaolNumber and not hasAffectedGUID[otherGaolNumber] then\n                                local otherGUID = otherGaolGUIDs[guidNumber]\n                                \n                                if otherGUID then\n                                    -- Swap GUIDs\n                                    gaolGUIDs[guidNumber] = otherGUID\n                                    otherGaolGUIDs[guidNumber] = GUID\n                                    \n                                    affectedPlayers = affectedPlayers - 1\n                                    \n                                    hasAffectedGUID[otherGaolNumber] = true\n                                    \n                                    swapped = true\n                                    \n                                    break\n                                end\n                            end\n                        end\n                        \n                        -- Try do a swap, but don't care if we actually swap with another player or just go to another gaol\n                        if not swapped then\n                            for otherGaolNumber, otherGaolGUIDs in ipairs(gaolAssignments) do\n                                -- Select a Gaol that doesn't have an affected GUID in it\n                                if gaolNumber ~= otherGaolNumber and not hasAffectedGUID[otherGaolNumber] then\n                                    local otherGUID = otherGaolGUIDs[guidNumber]\n                                    \n                                    -- Swap GUIDs\n                                    gaolGUIDs[guidNumber] = otherGUID\n                                    otherGaolGUIDs[guidNumber] = GUID\n                                    \n                                    affectedPlayers = affectedPlayers - 1\n                                    \n                                    hasAffectedGUID[otherGaolNumber] = true\n                                    \n                                    break\n                                end\n                            end\n                        end\n                    end\n                end\n            end\n        end\n        \n        -- Remove already assigned GUIDs from affected table\n        for _, gaolGUIDs in ipairs(gaolAssignments) do\n            for _, GUID in ipairs(gaolGUIDs) do\n                tDeleteItem(aura_env.affected, GUID)\n            end\n        end\n        \n        -- Assign affected players that are not in Gaols yet\n        for _, affectedGUID in ipairs(aura_env.affected) do\n            for gaolNumber, gaol in ipairs(gaolAssignments) do\n                if not hasAffectedGUID[gaolNumber] then\n                    table.insert(gaol, affectedGUID)\n                    \n                    hasAffectedGUID[gaolNumber] = true\n                    \n                    break\n                end\n            end\n        end\n        \n        -- Replace dead people?\n        \n        for gaolNumber, GUIDs in ipairs(gaolAssignments) do\n            for _, GUID in pairs(GUIDs) do\n                if GUID == WeakAuras.myGUID then\n                    aura_env.CreateDefaultStates(allstates, gaolCount)\n                    aura_env.Assign(allstates, gaolCount, gaolNumber, playerAffected, playerPreferredGaol and playerPreferredGaol ~= gaolNumber)\n                end\n            end\n        end\n        \n        WeakAuras.ScanEvents(\"LIQUID_GAOL_ASSIGN_ALL\", gaolAssignments)\n        \n        aura_env.affected = {}\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 472631 then -- Earthshaker Gaol\n            table.insert(aura_env.affected, destGUID)\n            \n            if #aura_env.affected == 1 then\n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.ReadNoteAssignments()\n        \n        aura_env.set = 0\n        aura_env.affected = {}\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
["events"] = "CLEU:SPELL_AURA_APPLIED, LIQUID_ASSIGNMENT_EVENT, ENCOUNTER_START",
