["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "status",
["custom_hide"] = "timed",
["event"] = "Health",
["unit"] = "player",
["names"] = {
},
["events"] = "NAME_PLATE_UNIT_ADDED, UNIT_TARGET:nameplate, CLEU:SPELL_AURA_REMOVED, ENCOUNTER_START, LIQUID_CLUSTER_MINE, RAID_TARGET_UPDATE, LIQUID_CUSTOM_EVENT",
["spellIds"] = {
},
["subeventPrefix"] = "SPELL",
["check"] = "event",
["custom"] = "function(event, ...)\n    if event == \"LIQUID_CUSTOM_EVENT\" then\n        local id, unit = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if not UnitExists(unit) then return end\n        \n        local GUID = UnitGUID(unit)\n        local npcID = GUID and select(6, strsplit(\"-\", GUID))\n        \n        if npcID == \"231788\" then -- Unstable Cluster Mine\n            local targetUnit = unit .. \"target\"\n            local targetGUID = targetUnit and UnitGUID(targetUnit)\n            local mark = GetRaidTargetIndex(unit)\n            local existingMineInfo = aura_env.mines[GUID]\n            \n            -- Only broadcast if info changed\n            if existingMineInfo and existingMineInfo.targetGUID == targetGUID and existingMineInfo.mark == mark then\n                return\n            end\n            \n            LiquidWeakAuras:Broadcast(\"LIQUID_CLUSTER_MINE\", GUID, targetGUID, mark)\n        end\n    elseif event == \"LIQUID_CLUSTER_MINE\" then\n        local mineGUID, targetGUID, mark = ...\n        \n        -- If this mine is dead (and we somehow received a late addon message), do nothing\n        if aura_env.deadMines[mineGUID] then return end\n        \n        aura_env.mines[mineGUID] = {\n            targetGUID = targetGUID,\n            mark = mark\n        }\n        \n        WeakAuras.ScanEvents(\"LIQUID_CLUSTER_MINE_LIST\", aura_env.mines)\n    elseif event == \"NAME_PLATE_UNIT_ADDED\" or event == \"UNIT_TARGET\" then\n        local unit = ...\n        \n        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id, unit)\n    elseif event == \"RAID_TARGET_UPDATE\" then\n        for _, t in pairs(C_NamePlate.GetNamePlates()) do\n            local unit = t.namePlateUnitToken\n            \n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id, unit)\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_REMOVED\" and spellID == 1219283 then -- Experimental Plating\n            aura_env.mines[destGUID] = nil\n            aura_env.deadMines[destGUID] = true\n            \n            WeakAuras.ScanEvents(\"LIQUID_CLUSTER_MINE_LIST\", aura_env.mines)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.mines = {} -- mineGUID = {targetGUID = \"\", mark = 0-8}\n        aura_env.deadMines = {} -- GUIDs of mines that are dead (safety for if we receive an addon message after the mine dies)\n    end\nend",
