["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = aura_env.duration,\n            expirationTime = GetTime() + aura_env.duration,\n            soak = math.random(2) == 1,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        if not aura_env.assignment then return end -- Never show if we are not assigned to soak anything\n        \n        local auraInfo = C_UnitAuras.GetPlayerAuraBySpellID(aura_env.vulnSpellID)\n        local expirationTime = auraInfo and auraInfo.expirationTime or 0\n        local canSoak = expirationTime < GetTime() + aura_env.duration\n        local shouldSoak = canSoak and aura_env.count == aura_env.assignment\n        \n        if shouldSoak and aura_env.config.soakTTS then\n            LiquidWeakAuras:PlaySound(\"soak\")\n        end\n        \n        if not shouldSoak and aura_env.config.dontSoakTTS then\n            LiquidWeakAuras:PlaySound(\"dont soak\")\n        end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = aura_env.duration,\n            expirationTime = GetTime() + aura_env.duration,\n            soak = shouldSoak,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if not aura_env.assignment then return end\n        \n        if spellID == aura_env.castSpellID then\n            aura_env.count = aura_env.count + 1\n            \n            if aura_env.count > aura_env.lines then\n                aura_env.count = 1\n            end\n            \n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.assignment = nil\n        aura_env.count = 0\n        \n        for lineCount, line in LiquidWeakAuras:IterateNoteAssignment() do\n            local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n            \n            if tContains(GUIDs, WeakAuras.myGUID) then\n                aura_env.assignment = lineCount\n            end\n            \n            aura_env.lines = lineCount\n        end\n    end\nend",
