["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6,\n            expirationTime = GetTime() + 6,\n            soak = math.random(2) == 1,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id, expirationTime = ...\n        \n        if id ~= aura_env.id then return end\n        if not next(aura_env.assignments) then return end -- Don't show if anything if no note was provided\n        \n        local GUIDs = aura_env.assignments[aura_env.count]\n        \n        if not GUIDs then return end\n        \n        local assigned = {}\n        \n        for _, GUID in ipairs(GUIDs) do\n            if #assigned == aura_env.playersPerSoak then break end\n            \n            local unit = aura_env.GUIDToUnit[GUID]\n            local alive = not UnitIsDeadOrGhost(unit)\n            local _, _, _, _, _, vulnExpirationTime = WA_GetUnitDebuff(unit, aura_env.vulnSpellID)\n            local canSoak = alive and (vulnExpirationTime or 0) < expirationTime\n            \n            if canSoak then\n                table.insert(assigned, GUID)\n            end\n        end\n        \n        local shouldSoak = tContains(assigned, WeakAuras.myGUID)\n        local stateExists = allstates[\"\"]\n        \n        if not stateExists and shouldSoak and aura_env.config.soakTTS then\n            LiquidWeakAuras:PlaySound(\"soak\")\n        end\n        \n        if not stateExists and not shouldSoak and aura_env.config.dontSoakTTS then\n            LiquidWeakAuras:PlaySound(\"dont soak\")\n        end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6,\n            expirationTime = expirationTime,\n            soak = shouldSoak,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID = ...\n        \n        -- If someone died while the assignment is showing, reassign in case they were assigned\n        if subEvent == \"UNIT_DIED\" and destGUID and aura_env.GUIDToUnit[destGUID] then\n            local state = allstates[\"\"]\n            \n            if state then\n                WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id, state.expirationTime)\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == aura_env.castSpellID then\n            aura_env.count = aura_env.count + 1\n            \n            if aura_env.count > #aura_env.assignments then\n                aura_env.count = 1\n            end\n            \n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"TRIGGER\" then\n        local _, triggerStates = ...\n        local _, triggerState = next(triggerStates or {})\n        \n        if not triggerState then\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n        \n        WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id, triggerState.expirationTime)\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.assignments = {}\n        aura_env.GUIDToUnit = {}\n        aura_env.count = 1\n        \n        for _, line in LiquidWeakAuras:IterateNoteAssignment() do\n            local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n            \n            if next(GUIDs) then\n                table.insert(aura_env.assignments, GUIDs)\n            end\n        end\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n    end\nend",
